   428→function secondsToCurrentTimecode(seconds) {
   429→    var seq = app.project.activeSequence;
   430→    if (seq) {
   431→        var t = new Time();
   432→        t.seconds = parseFloat(seconds);
   433→        var settings = app.project.activeSequence.getSettings();
   434→        return t.getFormatted(settings.videoFrameRate, settings.videoDisplayFormat);
