   482→function getActiveSequence() {
   483→    var seq = app.project.activeSequence;
   484→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   485→
   486→    return JSON.stringify({
   487→        name: seq.name,
   488→        id: seq.sequenceID,
   489→        duration: seq.end,
   490→        videoTrackCount: seq.videoTracks.numTracks,
   491→        audioTrackCount: seq.audioTracks.numTracks,
   492→        frameRate: seq.getSettings().videoFrameRate.seconds,
   493→        width: seq.frameSizeHorizontal,
   494→        height: seq.frameSizeVertical
   495→    });
