   501→
   502→    var settings = seq.getSettings();
   503→    return JSON.stringify({
   504→        frameRate: settings.videoFrameRate.seconds,
   505→        width: settings.videoFrameWidth,
   506→        height: settings.videoFrameHeight,
   507→        displayFormat: settings.videoDisplayFormat
