   511→function cloneSequence(newName) {
   512→    var seq = app.project.activeSequence;
   513→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   514→
   515→    try {
   516→        // Clone the sequence
   517→        seq.clone();
   518→
   519→        // Find the cloned sequence (last one with matching name pattern)
   520→        var clonedSeq = null;
   521→        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
   522→            var s = app.project.sequences[i];
   523→            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
   524→                clonedSeq = s;
   525→                break;
   526→            }
   527→        }
   528→
   529→        if (clonedSeq && newName) {
   530→            clonedSeq.name = newName;
   531→        }
   532→
   533→        return JSON.stringify({
   534→            success: true,
   535→            sequenceId: clonedSeq ? clonedSeq.sequenceID : null,
   536→            name: clonedSeq ? clonedSeq.name : newName
   537→        });
   538→    } catch (e) {
   539→        return JSON.stringify({ error: e.message });
   540→    }
   541→}
