   557→function setActiveSequence(sequenceId) {
   558→    for (var i = 0; i < app.project.sequences.numSequences; i++) {
   559→        if (app.project.sequences[i].sequenceID === sequenceId) {
   560→            app.project.activeSequence = app.project.sequences[i];
   561→            return JSON.stringify({ success: true });
   562→        }
   563→    }
   564→    return JSON.stringify({ error: "Sequence not found" });
