   567→/**
   568→ * Seek playhead to a specific time in seconds
   569→ */
   570→function seekToTime(seconds) {
   571→    var seq = app.project.activeSequence;
   572→    if (!seq) return JSON.stringify({ error: "No active sequence" });
   573→
   574→    try {
   575→        // Convert seconds to ticks
   576→        var ticks = seconds * TICKS_PER_SECOND;
   577→
   578→        // Create a Time object and set position
   579→        var playerPos = seq.getPlayerPosition();
   580→        playerPos.seconds = seconds;
   581→        seq.setPlayerPosition(playerPos.ticks.toString());
   582→
   583→        return JSON.stringify({ success: true, position: seconds });
   584→    } catch (e) {
   585→        return JSON.stringify({ error: e.message });
   586→    }
