  1351→
  1352→function buildSequenceFromCutList(cutListJson) {
  1353→    try {
  1354→        var cutList = JSON.parse(cutListJson);
  1355→        var seq = app.project.activeSequence;
  1356→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1357→
  1358→        // Clone the sequence
  1359→        var newSequenceName = seq.name + "_SPLICE";
  1360→        seq.clone();
  1361→
  1362→        // Find the cloned sequence
  1363→        var newSeq = null;
  1364→        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
  1365→            var s = app.project.sequences[i];
  1366→            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
  1367→                newSeq = s;
  1368→                break;
  1369→            }
  1370→        }
  1371→
  1372→        if (!newSeq) {
  1373→            return JSON.stringify({ error: "Failed to clone sequence" });
  1374→        }
  1375→
  1376→        newSeq.name = newSequenceName;
  1377→        app.project.activeSequence = newSeq;
  1378→
  1379→        // Clear all clips from the new sequence
  1380→        clearSequence();
  1381→
  1382→        // Insert segments
  1383→        var currentPosition = 0;
  1384→        var stats = {
  1385→            segmentsInserted: 0,
  1386→            totalDuration: 0
  1387→        };
  1388→
  1389→        for (var i = 0; i < cutList.segments.length; i++) {
  1390→            var segment = cutList.segments[i];
  1391→
  1392→            // Find the source project item
  1393→            var sourceItem = null;
  1394→            if (segment.sourcePath) {
  1395→                sourceItem = findItemByPath(app.project.rootItem, segment.sourcePath);
  1396→            }
  1397→            if (!sourceItem && segment.sourceName) {
  1398→                sourceItem = findItemByName(app.project.rootItem, segment.sourceName);
  1399→            }
  1400→
  1401→            if (!sourceItem) continue;
  1402→
  1403→            // Set color label
  1404→            if (segment.colorHint && SEGMENT_COLORS[segment.colorHint]) {
  1405→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.colorHint]);
  1406→            } else if (segment.type && SEGMENT_COLORS[segment.type]) {
  1407→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.type]);
  1408→            }
  1409→
  1410→            // Insert clip at current position
  1411→            var targetTime = new Time();
  1412→            targetTime.seconds = currentPosition;
  1413→
  1414→            newSeq.videoTracks[0].insertClip(sourceItem, targetTime);
  1415→
  1416→            // Set in/out points
  1417→            var clipDuration = segment.outPoint - segment.inPoint;
  1418→            var videoClips = newSeq.videoTracks[0].clips;
  1419→            var insertedClip = videoClips[videoClips.numItems - 1];
  1420→
  1421→            if (insertedClip) {
  1422→                var inTime = new Time();
  1423→                inTime.seconds = segment.inPoint;
  1424→                insertedClip.inPoint = inTime;
  1425→
  1426→                var outTime = new Time();
  1427→                outTime.seconds = segment.outPoint;
  1428→                insertedClip.outPoint = outTime;
  1429→
  1430→                // Set clip name if takeLabel provided
  1431→                if (segment.takeLabel) {
  1432→                    insertedClip.name = segment.takeLabel;
  1433→                }
  1434→            }
  1435→
  1436→            currentPosition += clipDuration;
  1437→            stats.segmentsInserted++;
  1438→            stats.totalDuration += clipDuration;
  1439→        }
  1440→
  1441→        return JSON.stringify({
  1442→            success: true,
  1443→            sequenceName: newSequenceName,
  1444→            stats: stats
  1445→        });
  1446→    } catch (e) {
  1447→        return JSON.stringify({ error: e.message });
  1448→    }
  1449→}
