  1707→    for (var i = 0; i < video_clips.numItems; i++) {
  1708→        var video_clip = video_clips[i];
  1709→        var video_start = video_clip.start.seconds;
  1710→
  1711→        for (var j = 0; j < audio_clips.numItems; j++) {
  1712→            var audio_clip = audio_clips[j];
  1713→            var audio_start = audio_clip.start.seconds;
  1714→
  1715→            if (Math.abs(video_start - audio_start) < time_tolerance) {
  1716→                video_clip.setSelected(true, false);
  1717→                audio_clip.setSelected(true, false);
  1718→                app.project.activeSequence.linkSelection();
  1719→                video_clip.setSelected(false, false);
  1720→                audio_clip.setSelected(false, false);
  1721→                break;
  1722→            }
  1723→        }
