  1735→        var comments = "Duration: " + duration + "s | Easing: " + (easing || "ease-in-out");
  1736→        return createMarker(startSeconds, name, duration, comments, COLOR_LABELS.YELLOW);
  1737→    } catch (e) {
  1738→        return JSON.stringify({ error: e.message });
  1739→    }
  1740→}
  1741→
  1742→// ============================================================================
  1743→// CHAPTER OPERATIONS (Phase 3)
  1744→// ============================================================================
  1745→
  1746→function addChapterMarker(timeSeconds, title, description) {
  1747→    try {
  1748→        var seq = app.project.activeSequence;
  1749→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1750→
  1751→        var markers = seq.markers;
  1752→        var marker = markers.createMarker(timeSeconds);
  1753→
  1754→        marker.name = title;
  1755→        if (description) marker.comments = description;
  1756→        marker.setColorByIndex(COLOR_LABELS.BLUE);
  1757→        marker.setTypeAsChapter();
  1758→
  1759→        return JSON.stringify({
  1760→            success: true,
  1761→            guid: marker.guid
  1762→        });
  1763→    } catch (e) {
  1764→        return JSON.stringify({ error: e.message });
  1765→    }
  1766→}
  1767→
  1768→function getChapterMarkers() {
  1769→    try {
  1770→        var seq = app.project.activeSequence;
  1771→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1772→
  1773→        var markers = seq.markers;
  1774→        var chapters = [];
  1775→
  1776→        for (var i = 0; i < markers.numMarkers; i++) {
  1777→            var marker = markers[i];
  1778→            if (marker.type === "Chapter") {
  1779→                chapters.push({
  1780→                    guid: marker.guid,
  1781→                    name: marker.name,
  1782→                    comments: marker.comments,
  1783→                    start: marker.start.seconds
  1784→                });
  1785→            }
  1786→        }
  1787→
  1788→        return JSON.stringify(chapters);
  1789→    } catch (e) {
  1790→        return JSON.stringify({ error: e.message });
  1791→    }
  1792→}
  1793→
  1794→// ============================================================================
  1795→// UNDO OPERATIONS
  1796→// ============================================================================
  1797→
  1798→function undo() {
  1799→    app.project.undo();
  1800→    return true;
  1801→}
  1802→
  1803→function redo() {
  1804→    app.project.redo();
  1805→    return true;
  1806→}
  1807→
  1808→// ============================================================================
  1809→// DEBUG / LOGGING
  1810→// ============================================================================
  1811→
  1812→function log(message) {
  1813→    $.writeln("[SPLICE] " + message);
  1814→}
  1815→
  1816→function getDebugInfo() {
  1817→    return JSON.stringify({
  1818→        version: SPLICE_VERSION,
  1819→        hasActiveSequence: app.project.activeSequence ? true : false,
  1820→        sequenceName: app.project.activeSequence ? app.project.activeSequence.name : null,
  1821→        numSequences: app.project.sequences.numSequences,
  1822→        numProjectItems: app.project.rootItem.children.numItems
  1823→    });
  1824→}
