/**
 * SPLICE CEP Host Script
 * ExtendScript for Premiere Pro
 *
 * Provides all Premiere Pro API functions called via jsx.evalScript()
 * Based on proven patterns from FireCut and SPLICE UXP builder
 */

// ============================================================================
// GLOBAL CONSTANTS & STATE
// ============================================================================
var SPLICE_VERSION = "6.0.3";
var TICKS_PER_SECOND = 254016000000;
var time_tolerance = 0.01; // 10ms tolerance for clip matching
var temp_bin = null;
var sequence_default_timecode = null;

// Color label indices (Premiere Pro standard)
var COLOR_LABELS = {
    NONE: 0,
    VIOLET: 1,
    IRIS: 2,
    CARIBBEAN: 3,
    LAVENDER: 4,
    CERULEAN: 5,
    FOREST: 6,
    ROSE: 7,
    MANGO: 8,
    PURPLE: 9,
    BLUE: 10,
    TEAL: 11,
    MAGENTA: 12,
    TAN: 13,
    GREEN: 14,
    BROWN: 15,
    YELLOW: 16
};

// Segment type to color mapping
var SEGMENT_COLORS = {
    speech: COLOR_LABELS.FOREST,
    take: COLOR_LABELS.LAVENDER,
    best_take: COLOR_LABELS.CERULEAN,
    silence: COLOR_LABELS.VIOLET,
    wide_shot: COLOR_LABELS.YELLOW,
    speaker_a: COLOR_LABELS.MANGO,
    speaker_b: COLOR_LABELS.CARIBBEAN
};

// Timecode display formats
var TIMECODES = {
    TIMEDISPLAY_Frames: 4
};

// Translation map for localized effect names
var translations = {
    "Motion": ["Motion", "Mouvement", "Bewegung", "Movimiento", "Movimento"],
    "Transform": ["Transform", "Transformation", "Transformieren", "Transformar"],
    "Scale": ["Scale", "Echelle", "Skalierung", "Escala"],
    "Scale Height": ["Scale Height", "Hauteur de l'echelle", "Skalierungshoehe"],
    "Scale Width": ["Scale Width", "Largeur de l'echelle", "Skalierungsbreite"],
    "Position": ["Position", "Posicion", "Posicao"],
    "Anchor Point": ["Anchor Point", "Point d'ancrage", "Ankerpunkt"],
    "Rotation": ["Rotation", "Rotacion", "Rotacao"],
    "Opacity": ["Opacity", "Opacite", "Deckkraft", "Opacidad"]
};

// Project item types
var project_item_type = {
    "clip": 1,
    "bin": 2,
    "root": 3,
    "file": 4
};

// ============================================================================
// SECURITY: INPUT VALIDATION UTILITIES (CEP-CRIT-001)
// Prevents path traversal, command injection, and null byte attacks
// ============================================================================

/**
 * Validate and sanitize file paths to prevent path traversal attacks
 * @param {string} path - File path to validate
 * @returns {Object} { valid: boolean, path: string, error: string }
 */
function validateFilePath(path) {
    if (!path || typeof path !== 'string') {
        return { valid: false, path: null, error: 'Invalid path: must be a non-empty string' };
    }

    // Check for null bytes (CVE potential)
    if (path.indexOf('\0') !== -1 || path.indexOf('\x00') !== -1) {
        return { valid: false, path: null, error: 'Invalid path: null bytes detected' };
    }

    // Normalize path separators
    var normalized = path.replace(/\\/g, '/');

    // Check for directory traversal attempts
    if (normalized.indexOf('../') !== -1 || normalized.indexOf('..\\') !== -1) {
        return { valid: false, path: null, error: 'Invalid path: directory traversal detected' };
    }

    // Check for potentially dangerous characters in paths (command injection)
    var dangerousChars = /[`$();|&<>]/;
    if (dangerousChars.test(path)) {
        return { valid: false, path: null, error: 'Invalid path: dangerous characters detected' };
    }

    // Check for protocol handlers that could be exploited
    var protocolPattern = /^[a-zA-Z][a-zA-Z0-9+.-]*:/;
    if (protocolPattern.test(path) && !path.match(/^[A-Z]:\//i)) {
        // Allow Windows drive letters (C:/) but block other protocols (javascript:, data:, etc.)
        return { valid: false, path: null, error: 'Invalid path: protocol handlers not allowed' };
    }

    return { valid: true, path: path, error: null };
}

/**
 * Validate sequence name to prevent XSS and injection
 * @param {string} name - Sequence name to validate
 * @returns {Object} { valid: boolean, name: string, error: string }
 */
function validateSequenceName(name) {
    if (!name || typeof name !== 'string') {
        return { valid: false, name: null, error: 'Invalid name: must be a non-empty string' };
    }

    // Check for null bytes
    if (name.indexOf('\0') !== -1) {
        return { valid: false, name: null, error: 'Invalid name: null bytes detected' };
    }

    // Check for potentially dangerous characters (file system reserved on Windows)
    var invalidChars = /[<>:"|?*]/;
    if (invalidChars.test(name)) {
        return { valid: false, name: null, error: 'Invalid name: contains reserved characters' };
    }

    // Limit length to prevent buffer overflows
    if (name.length > 255) {
        return { valid: false, name: null, error: 'Invalid name: exceeds maximum length (255)' };
    }

    return { valid: true, name: name, error: null };
}

/**
 * Validate numeric parameters to prevent injection and DoS
 * @param {*} value - Value to validate
 * @param {number} min - Minimum allowed value
 * @param {number} max - Maximum allowed value
 * @param {string} paramName - Parameter name for error messages
 * @returns {Object} { valid: boolean, value: number, error: string }
 */
function validateNumericParam(value, min, max, paramName) {
    if (value === undefined || value === null) {
        return { valid: false, value: null, error: paramName + ' is required' };
    }

    var num = parseFloat(value);
    if (isNaN(num)) {
        return { valid: false, value: null, error: paramName + ' must be a number' };
    }

    if (num < min || num > max) {
        return { valid: false, value: null, error: paramName + ' must be between ' + min + ' and ' + max };
    }

    return { valid: true, value: num, error: null };
}

/**
 * Sanitize string for safe use in Premiere Pro operations
 * @param {string} str - String to sanitize
 * @returns {string} Sanitized string
 */
function sanitizeString(str) {
    if (!str || typeof str !== 'string') {
        return '';
    }

    // Remove null bytes
    str = str.replace(/\0/g, '');

    // Escape special characters that could be problematic in ExtendScript
    str = str.replace(/[\r\n]/g, ' ');

    return str;
}

/**
 * Validate array of time values (for markers, cuts, etc.)
 * @param {Array} times - Array of time values
 * @param {number} maxLength - Maximum array length (DoS prevention)
 * @returns {Object} { valid: boolean, times: Array, error: string }
 */
function validateTimeArray(times, maxLength) {
    if (!times || !Array.isArray(times)) {
        return { valid: false, times: null, error: 'Times must be an array' };
    }

    maxLength = maxLength || 10000; // Default limit
    if (times.length > maxLength) {
        return { valid: false, times: null, error: 'Too many time values (max: ' + maxLength + ')' };
    }

    for (var i = 0; i < times.length; i++) {
        var validation = validateNumericParam(times[i], 0, Number.MAX_VALUE, 'time[' + i + ']');
        if (!validation.valid) {
            return { valid: false, times: null, error: validation.error };
        }
    }

    return { valid: true, times: times, error: null };
}

// ============================================================================
// INITIALIZATION
// ============================================================================

/**
 * Check if QE (Quality Engine) DOM is available
 * QE provides extended functionality not available in standard DOM
 */
function isQEAvailable() {
    try {
        return typeof qe !== 'undefined' && qe !== null && qe.project;
    } catch (e) {
        return false;
    }
}

/**
 * Enable QE DOM if not already enabled
 * @returns {boolean} true if QE is now available
 */
function enableQE() {
    try {
        if (isQEAvailable()) return true;
        app.enableQE();
        return isQEAvailable();
    } catch (e) {
        $.writeln('[SPLICE] enableQE failed: ' + e.message);
        return false;
    }
}

/**
 * Safe wrapper for QE operations
 * @param {Function} operation - The QE operation to execute
 * @param {string} operationName - Name for error reporting
 * @returns {Object} Result or error object
 */
function safeQEOperation(operation, operationName) {
    try {
        if (!isQEAvailable()) {
            if (!enableQE()) {
                return { error: "QE not available. Please ensure Premiere Pro supports QE DOM." };
            }
        }
        return operation();
    } catch (e) {
        return { error: operationName + " failed: " + e.message };
    }
}

function initialise() {
    storeDefaultTimecode();
    // Try to enable QE on startup
    var qeStatus = enableQE();
    return JSON.stringify({ success: true, version: SPLICE_VERSION, qeAvailable: qeStatus });
}

function storeDefaultTimecode() {
    if (app.project.activeSequence) {
        sequence_default_timecode = app.project.activeSequence.getSettings().videoDisplayFormat;
    }
}

/**
 * Check if a sequence is currently open/active in the Timeline
 * Uses multiple fallbacks to ensure accurate detection
 * @returns {boolean} true if a sequence is open
 */
function checkSequenceOpen() {
    try {
        $.writeln('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC START ==========');

        // Check if app exists
        if (!app) {
            $.writeln('[SPLICE] checkSequenceOpen: ERROR - app object does not exist');
            return false;
        }
        $.writeln('[SPLICE] checkSequenceOpen: app object exists');

        // Check if project exists
        if (!app.project) {
            $.writeln('[SPLICE] checkSequenceOpen: ERROR - app.project does not exist');
            return false;
        }
        $.writeln('[SPLICE] checkSequenceOpen: app.project exists');

        // Check if sequences collection exists
        if (!app.project.sequences) {
            $.writeln('[SPLICE] checkSequenceOpen: ERROR - app.project.sequences does not exist');
            return false;
        }
        $.writeln('[SPLICE] checkSequenceOpen: app.project.sequences exists');

        // Log total number of sequences
        var numSequences = app.project.sequences.numSequences;
        $.writeln('[SPLICE] checkSequenceOpen: Total sequences in project: ' + numSequences);

        // List all sequences with their details
        for (var i = 0; i < numSequences; i++) {
            var seq = app.project.sequences[i];
            if (seq) {
                $.writeln('[SPLICE] checkSequenceOpen:   - Sequence ' + i + ': name="' + seq.name + '", id=' + seq.sequenceID);
            }
        }

        // Primary check: Standard DOM - Check for activeSequence
        $.writeln('[SPLICE] checkSequenceOpen: Checking app.project.activeSequence...');
        if (app.project.activeSequence) {
            var activeSeq = app.project.activeSequence;
            $.writeln('[SPLICE] checkSequenceOpen: SUCCESS - Found active sequence via DOM');
            $.writeln('[SPLICE] checkSequenceOpen:   - name: ' + activeSeq.name);
            $.writeln('[SPLICE] checkSequenceOpen:   - sequenceID: ' + activeSeq.sequenceID);
            $.writeln('[SPLICE] checkSequenceOpen:   - videoTracks: ' + activeSeq.videoTracks.numTracks);
            $.writeln('[SPLICE] checkSequenceOpen:   - audioTracks: ' + activeSeq.audioTracks.numTracks);
            $.writeln('[SPLICE] checkSequenceOpen:   - frameSize: ' + activeSeq.frameSizeHorizontal + 'x' + activeSeq.frameSizeVertical);
            $.writeln('[SPLICE] checkSequenceOpen: ========== checkSequenceOpen DIAGNOSTIC END (TRUE) ==========');
            return true;
        }
        $.writeln('[SPLICE] checkSequenceOpen: app.project.activeSequence is null/undefined');

        // Fallback: Try QE DOM (requires enableQE first)
        $.writeln('[SPLICE] checkSequenceOpen: Trying QE DOM fallback...');
        if (enableQE()) {
            try {
                $.writeln('[SPLICE] checkSequenceOpen: QE DOM is enabled');
                var qeSeq = qe.project.getActiveSequence();
                if (qeSeq && qeSeq.name) {
                    $.writeln('[SPLICE] checkSequenceOpen: SUCCESS - Found active sequence via QE DOM: ' + qeSeq.name);
                    $.writeln('[SPLICE] checkSequenceOpen: ========== checkSequenceOpen DIAGNOSTIC END (TRUE) ==========');
                    return true;
                }
                $.writeln('[SPLICE] checkSequenceOpen: QE DOM returned null or sequence without name');
            } catch (qeErr) {
                $.writeln('[SPLICE] checkSequenceOpen: QE sequence check failed: ' + qeErr.message);
            }
        } else {
            $.writeln('[SPLICE] checkSequenceOpen: QE DOM is NOT available/enabled');
        }

        $.writeln('[SPLICE] checkSequenceOpen: No active sequence found in either DOM or QE DOM');
        $.writeln('[SPLICE] checkSequenceOpen: ========== checkSequenceOpen DIAGNOSTIC END (FALSE) ==========');
        return false;
    } catch (e) {
        $.writeln('[SPLICE] checkSequenceOpen: EXCEPTION - ' + e.message);
        $.writeln('[SPLICE] checkSequenceOpen: Stack: ' + (e.stack || 'no stack available'));
        $.writeln('[SPLICE] checkSequenceOpen: ========== checkSequenceOpen DIAGNOSTIC END (ERROR) ==========');
        return false;
    }
}

function getVersion() {
    return SPLICE_VERSION;
}

// ============================================================================
// TIME CONVERSION UTILITIES
// ============================================================================

/**
 * Get the actual frames per second from the sequence
 * videoFrameRate.seconds gives seconds per frame, so we need to invert it
 */
function getActualFrameRate() {
    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
    // secsPerFrame is e.g., 0.03333... for 30fps, so 1/secsPerFrame = 30
    return 1 / secsPerFrame;
}

function quantise_time(time_seconds) {
    // Get seconds per frame (e.g., 0.0333 for 30fps)
    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
    // Round to nearest frame boundary
    return secsPerFrame * Math.round(time_seconds / secsPerFrame);
}

function frames_to_time(number_of_frames) {
    // seconds per frame * number of frames = total seconds
    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
    return number_of_frames * secsPerFrame;
}

function frames_to_ticks(number_of_frames) {
    // Convert frames to ticks: frames * seconds_per_frame * ticks_per_second
    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
    return number_of_frames * secsPerFrame * TICKS_PER_SECOND;
}

function seconds_to_ticks(seconds) {
    return seconds * TICKS_PER_SECOND;
}

function ticks_to_seconds(ticks) {
    return ticks / TICKS_PER_SECOND;
}

function getVideoFrameRateInSeconds() {
    try {
        var seq = app.project.activeSequence;
        if (!seq) {
            $.writeln('[SPLICE] getVideoFrameRateInSeconds: No active sequence');
            return null;
        }
        return seq.getSettings().videoFrameRate.seconds;
    } catch (e) {
        $.writeln('[SPLICE] getVideoFrameRateInSeconds error: ' + e.message);
        return null;
    }
}

function secondsToCurrentTimecode(seconds) {
    var seq = app.project.activeSequence;
    if (seq) {
        var t = new Time();
        t.seconds = parseFloat(seconds);
        var settings = app.project.activeSequence.getSettings();
        return t.getFormatted(settings.videoFrameRate, settings.videoDisplayFormat);
    }
    return false;
}

// ============================================================================
// TIMECODE FORMAT
// ============================================================================

function setTimecodeToFrames() {
    var seq = app.project.activeSequence;
    if (seq) {
        var currentSeqSettings = app.project.activeSequence.getSettings();
        if (currentSeqSettings.videoDisplayFormat != TIMECODES.TIMEDISPLAY_Frames) {
            currentSeqSettings.videoDisplayFormat = TIMECODES.TIMEDISPLAY_Frames;
            app.project.activeSequence.setSettings(currentSeqSettings);
        }
    }
}

function setTimecodeToDefault() {
    var seq = app.project.activeSequence;
    if (seq && sequence_default_timecode) {
        var currentSeqSettings = app.project.activeSequence.getSettings();
        currentSeqSettings.videoDisplayFormat = sequence_default_timecode;
        app.project.activeSequence.setSettings(currentSeqSettings);
    }
}

function getTimecodeFormat() {
    return app.project.activeSequence.getSettings().videoDisplayFormat;
}

function setTimecodeFormat(format) {
    format = parseInt(format);
    var seq = app.project.activeSequence;
    if (seq) {
        var currentSeqSettings = app.project.activeSequence.getSettings();
        currentSeqSettings.videoDisplayFormat = format;
        app.project.activeSequence.setSettings(currentSeqSettings);
    }
    return true;
}

// ============================================================================
// SEQUENCE & PROJECT OPERATIONS
// ============================================================================

function getActiveSequence() {
    var seq = app.project.activeSequence;
    if (!seq) return JSON.stringify({ error: "No active sequence" });

    return JSON.stringify({
        name: seq.name,
        id: seq.sequenceID,
        duration: seq.end,
        videoTrackCount: seq.videoTracks.numTracks,
        audioTrackCount: seq.audioTracks.numTracks,
        frameRate: seq.getSettings().videoFrameRate.seconds,
        width: seq.frameSizeHorizontal,
        height: seq.frameSizeVertical
    });
}

function getSequenceSettings() {
    var seq = app.project.activeSequence;
    if (!seq) return JSON.stringify({ error: "No active sequence" });

    var settings = seq.getSettings();
    return JSON.stringify({
        frameRate: settings.videoFrameRate.seconds,
        width: settings.videoFrameWidth,
        height: settings.videoFrameHeight,
        displayFormat: settings.videoDisplayFormat
    });
}

function cloneSequence(newName) {
    var seq = app.project.activeSequence;
    if (!seq) return JSON.stringify({ error: "No active sequence" });

    try {
        // Clone the sequence
        seq.clone();

        // Find the cloned sequence (last one with matching name pattern)
        var clonedSeq = null;
        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
            var s = app.project.sequences[i];
            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
                clonedSeq = s;
                break;
            }
        }

        if (clonedSeq && newName) {
            clonedSeq.name = newName;
        }

        return JSON.stringify({
            success: true,
            sequenceId: clonedSeq ? clonedSeq.sequenceID : null,
            name: clonedSeq ? clonedSeq.name : newName
        });
    } catch (e) {
        return JSON.stringify({ error: e.message });
    }
}

function createNewSequence(name) {
    try {
        app.project.createNewSequence(name);
        var seq = app.project.activeSequence;
        return JSON.stringify({
            success: true,
            sequenceId: seq.sequenceID,
            name: seq.name
        });
    } catch (e) {
        return JSON.stringify({ error: e.message });
    }
}

function setActiveSequence(sequenceId) {
    for (var i = 0; i < app.project.sequences.numSequences; i++) {
        if (app.project.sequences[i].sequenceID === sequenceId) {
            app.project.activeSequence = app.project.sequences[i];
            return JSON.stringify({ success: true });
        }
    }
    return JSON.stringify({ error: "Sequence not found" });
}

/**
 * Seek playhead to a specific time in seconds
 */
function seekToTime(seconds) {
    var seq = app.project.activeSequence;
    if (!seq) return JSON.stringify({ error: "No active sequence" });

    try {
        // Convert seconds to ticks
        var ticks = seconds * TICKS_PER_SECOND;

        // Create a Time object and set position
        var playerPos = seq.getPlayerPosition();
        playerPos.seconds = seconds;
        seq.setPlayerPosition(playerPos.ticks.toString());

        return JSON.stringify({ success: true, position: seconds });
    } catch (e) {
        return JSON.stringify({ error: e.message });
    }
}

/**
 * Get current playhead position in seconds
 */
function getPlayerPosition() {
    var seq = app.project.activeSequence;
    if (!seq) return JSON.stringify({ error: "No active sequence" });

    try {
        var pos = seq.getPlayerPosition();
        return JSON.stringify({
            ticks: pos.ticks,
            seconds: pos.seconds
        });
    } catch (e) {
        return JSON.stringify({ error: e.message });
    }
}

// ============================================================================
// RAZOR OPERATIONS
// ============================================================================

function razorSequenceAtFrames(frames) {
    if (!isQEAvailable() && !enableQE()) {
        return JSON.stringify({ error: "QE not available for razor operation" });
    }
    try {
        frames = String(frames);
        setTimecodeToFrames();
        qe.project.getActiveSequence().razor(frames);
        return JSON.stringify({ success: true });
    } catch (e) {
        return JSON.stringify({ error: "Razor failed: " + e.message });
    }
}

function razorSequenceAtSeconds(seconds) {
    try {
        var frames = parseInt(Math.round(seconds / app.project.activeSequence.getSettings().videoFrameRate.seconds)).toString();
        return razorSequenceAtFrames(frames);
    } catch (e) {
        return JSON.stringify({ error: "Razor at seconds failed: " + e.message });
    }
}

function razorSequenceAtFramesArray(silences_array) {
    if (!isQEAvailable() && !enableQE()) {
        return JSON.stringify({ error: "QE not available for razor operation" });
    }
    try {
        setTimecodeToFrames();
        silences_array = JSON.parse(silences_array);
        var razorCount = 0;
        for (var i = 0; i < silences_array.length; i++) {
            for (var j = 0; j < silences_array[i].length; j++) {
                var frames = String(silences_array[i][j]);
                qe.project.getActiveSequence().razor(frames);
                razorCount++;
            }
        }
        return JSON.stringify({ success: true, razorCount: razorCount });
    } catch (e) {
        return JSON.stringify({ error: "Razor array failed: " + e.message });
    }
}

function razorTrackAtFrames(trackType, trackIndex, frames) {
    if (!isQEAvailable() && !enableQE()) {
        return JSON.stringify({ error: "QE not available for track razor" });
    }
    try {
        frames = String(frames);
        setTimecodeToFrames();
        if (trackType == "video") {
