function deleteClipsAtSilencePointsInTrack(silences, trackType, trackIndex) {
    try {
        var seq = app.project.activeSequence;
        if (!seq) return JSON.stringify({ error: "No active sequence" });

        // Validate trackType
        if (trackType !== "video" && trackType !== "audio") {
            return JSON.stringify({ error: "Invalid track type: " + trackType });
        }

        // Parse silences JSON
        var silencesArr;
        try {
            silencesArr = JSON.parse(silences);
        } catch (parseErr) {
            return JSON.stringify({ error: "Invalid silences JSON: " + parseErr.message });
        }

        // Get tracks and validate index
        var tracks = trackType === "video" ? seq.videoTracks : seq.audioTracks;
        if (trackIndex < 0 || trackIndex >= tracks.numTracks) {
            return JSON.stringify({ error: "Invalid track index: " + trackIndex + " (max: " + (tracks.numTracks - 1) + ")" });
        }

        var clips = tracks[trackIndex].clips;
        var deleted = 0;

        // Iterate backwards to avoid index shifting (use numItems, not length)
        for (var i = clips.numItems - 1; i >= 0; i--) {
            var clip = clips[i];
            var start = clip.start.seconds;
            var end = clip.end.seconds;
            var center = (start + end) / 2;

            for (var j = 0; j < silencesArr.length; j++) {
                var silence = silencesArr[j];
                var silenceCenter = (silence[0] + silence[1]) / 2;
                if (silenceCenter > start && silenceCenter < end) {
                    clip.remove(false, false);
                    deleted++;
                    break;
                }
            }
        }

        $.writeln('[SPLICE] deleteClipsAtSilencePointsInTrack: Deleted ' + deleted + ' clips from ' + trackType + ' track ' + trackIndex);
        return JSON.stringify({ deleted: deleted });
    } catch (e) {
        $.writeln('[SPLICE] deleteClipsAtSilencePointsInTrack error: ' + e.message);
        return JSON.stringify({ error: "deleteClipsAtSilencePointsInTrack failed: " + e.message });
    }
}

function makeRemainingClipsContiguousInTrack(silences, deltas, trackType, trackIndex) {
    silences = JSON.parse(silences);
    deltas = JSON.parse(deltas);

    var clips;
    if (trackType == "video") {
        clips = app.project.activeSequence.videoTracks[trackIndex].clips;
    } else if (trackType == "audio") {
        clips = app.project.activeSequence.audioTracks[trackIndex].clips;
    }

    for (var i = 0; i < clips.length; i++) {
        var clip = clips[i];
        var end = clip.end.seconds;
        var num_silences = -1;

        for (var j = 0; j < silences.length; j++) {
            if (end > silences[j][1]) {
                num_silences++;
            }
        }

        if (num_silences > -1 && num_silences < deltas.length) {
            var delta = deltas[num_silences];
            var t_start = clip.start;
            t_start.seconds = t_start.seconds - delta;
            clip.start = t_start;
            var t_end = clip.end;
            t_end.seconds = t_end.seconds - delta;
            clip.end = t_end;
        }
    }
    return JSON.stringify({ success: true });
}

