   392→/**
   393→ * Initialize preset selector
   394→ */
   395→function initPresetSelector() {
   396→  const presetSelector = document.getElementById('presetSelector');
   397→  const sensitivitySlider = document.getElementById('sensitivitySlider');
   398→
   399→  if (!presetSelector) return;
   400→
   401→  // Set initial value from settings
   402→  const settings = getSettings();
   403→  presetSelector.value = settings.activePreset || 'custom';
   404→
   405→  // Handle preset change
   406→  presetSelector.addEventListener('change', () => {
   407→    const presetName = presetSelector.value;
   408→    const appliedSettings = applyPreset(presetName);
   409→
   410→    // Update sensitivity slider to match preset
   411→    if (sensitivitySlider && appliedSettings.sensitivity !== undefined) {
   412→      sensitivitySlider.value = appliedSettings.sensitivity;
   413→    }
   414→
   415→    // Update takes detection checkbox
   416→    const enableTakesDetection = document.getElementById('enableTakesDetection');
   417→    if (enableTakesDetection && appliedSettings.enableTakesDetection !== undefined) {
   418→      enableTakesDetection.checked = appliedSettings.enableTakesDetection;
   419→    }
   420→
   421→    // Update auto-mark best checkbox
   422→    const autoMarkBest = document.getElementById('autoMarkBest');
   423→    if (autoMarkBest && appliedSettings.autoMarkBest !== undefined) {
   424→      autoMarkBest.checked = appliedSettings.autoMarkBest;
   425→    }
   426→
   427→    if (typeof setStatus === 'function') {
   428→      const preset = getPreset(presetName);
   429→      setStatus(`Preset: ${preset?.name || presetName} - ${preset?.description || ''}`);
   430→    }
   431→  });
   432→
   433→  // Switch to custom when user manually changes sensitivity
   434→  if (sensitivitySlider) {
   435→    sensitivitySlider.addEventListener('change', () => {
   436→      if (presetSelector.value !== 'custom') {
   437→        presetSelector.value = 'custom';
   438→        saveSettings({ activePreset: 'custom' });
   439→      }
   440→    });
   441→  }
   442→}
   443→
   444→/**
   445→ * Initialize help button
   446→ */
   447→function initHelpButton() {
   448→  const helpBtn = document.getElementById('helpBtn');
   449→  if (helpBtn) {
   450→    helpBtn.addEventListener('click', () => {
   451→      setStatus('Silence: removes quiet gaps | Takes: detects repeated content');
   452→    });
   453→  }
   454→}
