   456→// =============================================================================
   457→// PERSISTENT TOKEN MANAGEMENT (v3.5)
   458→// =============================================================================
   459→
   460→/**
   461→ * Media folder entry (cached after selection)
   462→ * @type {Object|null}
   463→ */
   464→let mediaFolderEntry = null;
   465→
   466→/**
   467→ * Prompt user to select a media folder and create a persistent token.
   468→ * This allows silent file access without repeated permission prompts.
   469→ *
   470→ * @returns {Promise<{success: boolean, path?: string, error?: string}>}
   471→ */
   472→async function setupMediaFolder() {
   473→  try {
   474→    // Open folder picker dialog
   475→    const folder = await uxpFs.getFolder();
   476→
   477→    if (!folder) {
   478→      return { success: false, error: 'No folder selected' };
   479→    }
   480→
   481→    // Create a persistent token for this folder
   482→    const token = await uxpFs.createPersistentToken(folder);
   483→
   484→    if (!token) {
   485→      return { success: false, error: 'Failed to create persistent token' };
   486→    }
   487→
   488→    // Get the folder path for display
   489→    const folderPath = folder.nativePath || folder.name;
   490→
   491→    // Save to settings
   492→    saveSettings({
   493→      mediaFolderToken: token,
   494→      mediaFolderPath: folderPath
   495→    });
   496→
   497→    // Cache the folder entry
   498→    mediaFolderEntry = folder;
   499→
   500→    console.log(`[SPLICE] Media folder set: ${folderPath}`);
   501→
   502→    return {
   503→      success: true,
   504→      path: folderPath,
   505→      token
   506→    };
   507→  } catch (err) {
   508→    console.error('[SPLICE] Error setting up media folder:', err);
   509→    return { success: false, error: err.message };
   510→  }
   511→}
   512→
   513→/**
   514→ * Get the media folder entry using the stored persistent token.
   515→ * Falls back to prompting if token is invalid.
   516→ *
   517→ * @returns {Promise<Object|null>} The folder entry or null
   518→ */
   519→async function getMediaFolder() {
   520→  // Return cached entry if available
   521→  if (mediaFolderEntry) {
   522→    return mediaFolderEntry;
   523→  }
   524→
   525→  const settings = getSettings();
   526→
   527→  // Try to restore from persistent token
   528→  if (settings.mediaFolderToken) {
   529→    try {
   530→      const folder = await uxpFs.getEntryForPersistentToken(settings.mediaFolderToken);
   531→      if (folder) {
   532→        mediaFolderEntry = folder;
   533→        console.log(`[SPLICE] Restored media folder from token: ${settings.mediaFolderPath}`);
   534→        return folder;
   535→      }
   536→    } catch (err) {
   537→      console.warn('[SPLICE] Could not restore media folder from token:', err.message);
   538→      // Clear invalid token
   539→      saveSettings({ mediaFolderToken: null, mediaFolderPath: null });
   540→    }
   541→  }
   542→
   543→  return null;
   544→}
   545→
   546→/**
   547→ * Check if a media folder is configured
   548→ * @returns {boolean}
   549→ */
   550→function hasMediaFolder() {
   551→  const settings = getSettings();
   552→  return !!settings.mediaFolderToken;
   553→}
   554→
   555→/**
   556→ * Get the configured media folder path (for display)
   557→ * @returns {string|null}
   558→ */
   559→function getMediaFolderPath() {
   560→  const settings = getSettings();
   561→  return settings.mediaFolderPath;
   562→}
   563→
   564→/**
   565→ * Clear the media folder configuration
   566→ */
   567→function clearMediaFolder() {
   568→  mediaFolderEntry = null;
   569→  saveSettings({
   570→    mediaFolderToken: null,
   571→    mediaFolderPath: null
   572→  });
   573→  console.log('[SPLICE] Media folder cleared');
   574→}
   575→
   576→/**
   577→ * Read a file from the media folder
   578→ * @param {string} filename - Name of the file to read
   579→ * @returns {Promise<ArrayBuffer|null>} File contents or null
   580→ */
   581→async function readMediaFile(filename) {
   582→  const folder = await getMediaFolder();
   583→  if (!folder) {
   584→    console.warn('[SPLICE] No media folder configured');
   585→    return null;
   586→  }
   587→
   588→  try {
   589→    const file = await folder.getEntry(filename);
   590→    if (!file) {
   591→      console.warn(`[SPLICE] File not found in media folder: ${filename}`);
   592→      return null;
   593→    }
   594→
   595→    const contents = await file.read({ format: uxpFs.formats.binary });
   596→    return contents;
   597→  } catch (err) {
   598→    console.error(`[SPLICE] Error reading file ${filename}:`, err);
   599→    return null;
   600→  }
   601→}
