   603→// =============================================================================
   604→// LOGIN MODAL
   605→// =============================================================================
   606→
   607→/**
   608→ * Initialize login modal handlers
   609→ */
   610→function initLoginModal() {
   611→  const loginModal = document.getElementById('loginModal');
   612→  const closeLoginBtn = document.getElementById('closeLoginBtn');
   613→  const saveLoginBtn = document.getElementById('saveLoginBtn');
   614→  const customerIdInput = document.getElementById('customerIdInput');
   615→  const loginError = document.getElementById('loginError');
   616→  const creditBadge = document.getElementById('creditBadge');
   617→
   618→  // Show login modal when clicking credit badge (if not logged in)
   619→  if (creditBadge) {
   620→    creditBadge.addEventListener('click', () => {
   621→      const settings = getSettings();
   622→      if (!settings.customerId) {
   623→        showLoginModal();
   624→      }
   625→    });
   626→  }
   627→
   628→  // Close login modal
   629→  if (closeLoginBtn && loginModal) {
   630→    closeLoginBtn.addEventListener('click', () => {
   631→      loginModal.classList.add('hidden');
   632→    });
   633→  }
   634→
   635→  // Close on backdrop click
   636→  if (loginModal) {
   637→    loginModal.addEventListener('click', (e) => {
   638→      if (e.target === loginModal) {
   639→        loginModal.classList.add('hidden');
   640→      }
   641→    });
   642→  }
   643→
   644→  // Save customer ID
   645→  if (saveLoginBtn) {
   646→    saveLoginBtn.addEventListener('click', async () => {
   647→      const customerId = customerIdInput?.value?.trim();
   648→
   649→      if (!customerId) {
   650→        showLoginError('Please enter your customer ID');
   651→        return;
   652→      }
   653→
   654→      if (!customerId.startsWith('cus_')) {
   655→        showLoginError('Customer ID should start with "cus_"');
   656→        return;
   657→      }
   658→
   659→      // Save to settings
   660→      saveSettings({ customerId });
   661→
   662→      // Hide modal
   663→      loginModal?.classList.add('hidden');
   664→
   665→      // Refresh credits display
   666→      if (typeof refreshCredits === 'function') {
   667→        await refreshCredits();
   668→      }
   669→
   670→      if (typeof setStatus === 'function') {
   671→        setStatus('Logged in successfully');
   672→      }
   673→    });
   674→  }
   675→
   676→  // Load existing customer ID into input
   677→  const settings = getSettings();
   678→  if (customerIdInput && settings.customerId) {
   679→    customerIdInput.value = settings.customerId;
   680→  }
   681→}
   682→
   683→/**
   684→ * Show login modal
   685→ */
   686→function showLoginModal() {
   687→  const loginModal = document.getElementById('loginModal');
   688→  const loginError = document.getElementById('loginError');
   689→
   690→  if (loginError) {
   691→    loginError.style.display = 'none';
   692→  }
   693→
   694→  if (loginModal) {
   695→    loginModal.classList.remove('hidden');
   696→  }
   697→}
   698→
   699→/**
   700→ * Show login error message
