    frequency: 0,
    description: 'Volume reduction (no beep)'
  },
  'mute': {
    name: 'Complete Mute',
    frequency: 0,
    description: 'Complete silence'
  }
};

/**
 * Get list of available bleep sounds
 */
function getAvailableBleepSounds() {
  return Object.entries(BLEEP_SOUNDS).map(([id, info]) => ({
    id,
    ...info
  }));
