// =============================================================================
// Bleep Audio Generation
// =============================================================================

/**
 * Generate a bleep/censor tone audio file
 *
 * @param {Object} options - Generation options
 * @param {number} options.duration - Duration in seconds
 * @param {string} options.bleepType - Type of bleep ('standard', 'tv', 'radio', 'duck', 'mute')
 * @param {number} options.sampleRate - Sample rate (default: 44100)
 * @param {number} options.volume - Volume level 0-1 (default: 0.5)
 * @param {string} options.outputPath - Optional output path (generates temp file if not provided)
 * @returns {Promise<Object>} Result with path to generated file
 */
async function generateBleepAudio(options = {}) {
  const {
    duration = 1.0,
    bleepType = 'standard',
    sampleRate = 44100,
    volume = 0.5,
    outputPath = null
  } = options;

  const bleepConfig = BLEEP_SOUNDS[bleepType] || BLEEP_SOUNDS.standard;
  const frequency = bleepConfig.frequency;

  console.log(`[SPLICE Bleep] Generating ${bleepType} bleep: ${duration}s @ ${frequency}Hz`);

  // Calculate number of samples
  const numSamples = Math.ceil(duration * sampleRate);
  const numChannels = 1; // Mono
  const bitsPerSample = 16;
  const bytesPerSample = bitsPerSample / 8;

  // Generate audio samples
  const samples = new Int16Array(numSamples);

  if (frequency > 0) {
    // Generate sine wave at specified frequency
    const angularFrequency = 2 * Math.PI * frequency / sampleRate;
    const amplitude = Math.floor(32767 * volume);

    // Apply fade in/out to avoid clicks (10ms each)
    const fadeLength = Math.min(Math.floor(0.01 * sampleRate), numSamples / 4);

    for (let i = 0; i < numSamples; i++) {
      // Base sine wave
      let sample = Math.sin(angularFrequency * i) * amplitude;

      // Apply fade in
      if (i < fadeLength) {
        sample *= i / fadeLength;
      }

      // Apply fade out
      if (i > numSamples - fadeLength) {
        sample *= (numSamples - i) / fadeLength;
      }

      samples[i] = Math.round(sample);
    }
  } else {
    // Mute/duck: generate silence
    samples.fill(0);
  }

  // Create WAV file
  const dataSize = numSamples * numChannels * bytesPerSample;
  const fileSize = 44 + dataSize; // 44-byte WAV header + data

  const buffer = Buffer.alloc(fileSize);

  // WAV header
  buffer.write('RIFF', 0);
  buffer.writeUInt32LE(fileSize - 8, 4);
  buffer.write('WAVE', 8);
  buffer.write('fmt ', 12);
  buffer.writeUInt32LE(16, 16); // Subchunk1Size (16 for PCM)
  buffer.writeUInt16LE(1, 20); // AudioFormat (1 = PCM)
  buffer.writeUInt16LE(numChannels, 22);
  buffer.writeUInt32LE(sampleRate, 24);
  buffer.writeUInt32LE(sampleRate * numChannels * bytesPerSample, 28); // ByteRate
  buffer.writeUInt16LE(numChannels * bytesPerSample, 32); // BlockAlign
  buffer.writeUInt16LE(bitsPerSample, 34);
  buffer.write('data', 36);
  buffer.writeUInt32LE(dataSize, 40);

  // Write samples
  for (let i = 0; i < numSamples; i++) {
    buffer.writeInt16LE(samples[i], 44 + i * bytesPerSample);
  }

  // Write to file
  const filePath = outputPath || path.join('/tmp', `splice_bleep_${crypto.randomBytes(4).toString('hex')}.wav`);

  await fs.promises.writeFile(filePath, buffer);

  console.log(`[SPLICE Bleep] Generated: ${filePath}`);

  return {
    success: true,
    path: filePath,
    duration,
    frequency,
    bleepType,
    sampleRate,
    fileSize
  };
}

/**
 * Generate bleep audio for multiple segments
 *
 * @param {Array} segments - Array of profanity segments with start/end
 * @param {Object} options - Generation options
 * @returns {Promise<Array>} Array of generated bleep info for each segment
 */
async function generateBleepsForSegments(segments, options = {}) {
  const { bleepType = 'standard', volume = 0.5, outputDir = '/tmp' } = options;

  const results = [];

  for (let i = 0; i < segments.length; i++) {
    const segment = segments[i];
    const duration = segment.end - segment.start;

    const outputPath = path.join(outputDir, `splice_bleep_segment_${i}_${crypto.randomBytes(4).toString('hex')}.wav`);

    try {
      const result = await generateBleepAudio({
        duration,
        bleepType,
        volume,
        outputPath
      });

      results.push({
        segmentIndex: i,
        segmentStart: segment.start,
        segmentEnd: segment.end,
        ...result
      });
    } catch (err) {
      console.error(`[SPLICE Bleep] Failed to generate bleep for segment ${i}:`, err);
      results.push({
        segmentIndex: i,
        success: false,
        error: err.message
      });
    }
  }

  return results;
}

/**
 * Clean up generated bleep files
 *
 * @param {Array} bleepPaths - Array of bleep file paths to delete
 */
async function cleanupBleepFiles(bleepPaths) {
  for (const filePath of bleepPaths) {
    try {
      await fs.promises.unlink(filePath);
    } catch (err) {
      // Ignore errors (file may already be deleted)
    }
  }
}

// =============================================================================
// Profanity List Management
// =============================================================================

/**
 * Get the default profanity list for a language
 *
 * @param {string} language - Language code (en, es, fr, de)
 * @returns {Array<string>} Profanity word list
 */
function getProfanityList(language = 'en') {
  return PROFANITY_LISTS[language] || PROFANITY_LISTS['en'];
}

/**
 * Get all supported languages
 */
function getSupportedLanguages() {
  return [
    { code: 'en', name: 'English' },
    { code: 'es', name: 'Spanish' },
    { code: 'fr', name: 'French' },
    { code: 'de', name: 'German' }
  ];
}

/**
 * Validate and clean a custom word list
 *
 * @param {string|Array} input - Comma-separated string or array
 * @returns {Array<string>} Cleaned word list
 */
function parseWordList(input) {
  if (!input) return [];

  if (Array.isArray(input)) {
    return input.map(w => w.trim().toLowerCase()).filter(w => w.length > 0);
  }

  if (typeof input === 'string') {
    return input
      .split(',')
      .map(w => w.trim().toLowerCase())
      .filter(w => w.length > 0);
  }

  return [];
}

// =============================================================================
// Segment Operations
// =============================================================================

/**
 * Merge profanity segments that are close together
 *
 * @param {Array} segments - Array of profanity segments
 * @param {number} maxGap - Maximum gap to merge (seconds)
 * @returns {Array} Merged segments
 */
function mergeAdjacentSegments(segments, maxGap = 0.5) {
  if (segments.length <= 1) return segments;

  const sorted = [...segments].sort((a, b) => a.start - b.start);
  const merged = [];
  let current = { ...sorted[0] };

  for (let i = 1; i < sorted.length; i++) {
    const next = sorted[i];

    if (next.start - current.end <= maxGap) {
      // Merge
      current.end = next.end;
      current.duration = current.end - current.start;
      current.words = [...current.words, ...next.words];
      current.wordCount = current.words.length;
      current.text = current.words.join(' ');
    } else {
      merged.push(current);
      current = { ...next };
    }
  }

  merged.push(current);
  return merged;
}

/**
 * Filter segments by user selection (for UI toggle)
 *
 * @param {Array} segments - All profanity segments
 * @param {Array<number>} selectedIndices - Indices of segments to keep
 * @returns {Array} Filtered segments
 */
function filterSelectedSegments(segments, selectedIndices) {
  const indexSet = new Set(selectedIndices);
  return segments.filter((_, i) => indexSet.has(i));
}

/**
 * Convert segments to timeline format for cut list generator
 *
 * @param {Array} segments - Profanity segments
 * @returns {Array<{start: number, end: number, type: string}>} Timeline-ready segments
 */
function segmentsToTimeline(segments) {
  return segments.map(seg => ({
    start: seg.start,
    end: seg.end,
    duration: seg.duration,
    type: 'profanity',
    label: `Profanity: ${seg.text.substring(0, 30)}${seg.text.length > 30 ? '...' : ''}`
  }));
}

// =============================================================================
// Exports
// =============================================================================

module.exports = {
  // Core detection
  detectProfanity,
  groupProfanitySegments,

  // Profanity lists
  getProfanityList,
  getSupportedLanguages,
  parseWordList,
  PROFANITY_LISTS,

  // Bleep sounds
  getAvailableBleepSounds,
  BLEEP_SOUNDS,

  // Bleep audio generation
  generateBleepAudio,
  generateBleepsForSegments,
  cleanupBleepFiles,

  // Segment operations
  mergeAdjacentSegments,
  filterSelectedSegments,
  segmentsToTimeline,

  // Utilities
  alignToFrame
};

