   335→// ============================================================================
   336→// TAKE-AWARE SILENCE FILTERING
   337→// ============================================================================
   338→/**
   339→ * Filter silences to protect those that overlap with takes (speech segments).
   340→ * Returns { safe: [], protected: [] }
   341→ */
   342→function filterSilencesByTakes(silences, takes) {
   343→  if (!takes || takes.length === 0) {
   344→    // No takes = all silences are safe to remove
   345→    return { safe: silences, protected: [] };
   346→  }
   347→
   348→  const safe = [];
   349→  const protected_ = [];
   350→
   351→  silences.forEach(silence => {
   352→    // Check if this silence overlaps with any take
   353→    const overlaps = takes.some(take => {
   354→      const takeStart = take.startTime || take.start;
   355→      const takeEnd = take.endTime || take.end;
   356→      // Overlap exists if silence starts before take ends AND silence ends after take starts
   357→      return silence.start < takeEnd && silence.end > takeStart;
   358→    });
   359→
   360→    if (overlaps) {
   361→      protected_.push(silence);
   362→    } else {
   363→      safe.push(silence);
   364→    }
   365→  });
   366→
   367→  console.log(`[SPLICE] Filtered silences: ${safe.length} safe, ${protected_.length} protected`);
   368→  return { safe, protected: protected_ };
   369→}
