   371→// ============================================================================
   372→// TIMELINE MARKERS (Visual indicators on Premiere Pro timeline)
   373→// ============================================================================
   374→/**
   375→ * Add colored markers to the timeline for silences and takes.
   376→ * - Red markers: silences safe to remove
   377→ * - Yellow markers: silences protected by takes
   378→ * - Blue markers: takes (speech segments)
   379→ *
   380→ * @param {Array} safeToRemove - Silences safe to remove
   381→ * @param {Array} protectedSilences - Silences overlapping speech
   382→ * @param {Array} takes - Detected speech segments
   383→ * @returns {Promise<{added: number, errors: number}>}
   384→ */
   385→async function addTimelineMarkers(safeToRemove, protectedSilences, takes) {
   386→  const results = { added: 0, errors: 0 };
   387→
   388→  try {
   389→    const context = await getActiveSequence();
   390→    if (!context) {
   391→      console.warn('[SPLICE] No sequence available for markers');
   392→      return results;
   393→    }
   394→
   395→    const { sequence, project } = context;
   396→
   397→    // Get markers object for the sequence
   398→    const markers = await ppro.Markers.getMarkers(sequence);
   399→    if (!markers) {
   400→      console.warn('[SPLICE] Markers API not available');
   401→      return results;
   402→    }
   403→
   404→    // Collect all marker actions
   405→    const markerActions = [];
   406→
   407→    // Red markers for safe-to-remove silences
   408→    for (const silence of safeToRemove) {
   409→      try {
   410→        const duration = silence.end - silence.start;
   411→
   412→        // Use TickTime API directly (no TICKS_PER_SECOND needed)
   413→        const startTime = ppro.TickTime.createWithSeconds(silence.start);
   414→        const durationTime = ppro.TickTime.createWithSeconds(duration);
   415→
   416→        const action = markers.createAddMarkerAction(
   417→          `SPLICE: Remove (${duration.toFixed(2)}s)`,
   418→          ppro.Marker.MARKER_TYPE_COMMENT,
   419→          startTime,
   420→          durationTime,
   421→          `Silence to remove: ${formatTime(silence.start)} - ${formatTime(silence.end)}`
   422→        );
   423→        markerActions.push({ action, colorIndex: 1, type: 'silence' }); // Red = index 1
   424→      } catch (err) {
   425→        console.error('[SPLICE] Error creating silence marker:', err);
   426→        results.errors++;
   427→      }
   428→    }
   429→
   430→    // Yellow markers for protected silences
   431→    for (const silence of protectedSilences) {
   432→      try {
   433→        const duration = silence.end - silence.start;
   434→
   435→        // Use TickTime API directly (no TICKS_PER_SECOND needed)
   436→        const startTime = ppro.TickTime.createWithSeconds(silence.start);
   437→        const durationTime = ppro.TickTime.createWithSeconds(duration);
   438→
   439→        const action = markers.createAddMarkerAction(
   440→          `SPLICE: Protected (${duration.toFixed(2)}s)`,
   441→          ppro.Marker.MARKER_TYPE_COMMENT,
   442→          startTime,
   443→          durationTime,
   444→          `Protected silence (overlaps speech): ${formatTime(silence.start)} - ${formatTime(silence.end)}`
   445→        );
   446→        markerActions.push({ action, colorIndex: 4, type: 'protected' }); // Yellow = index 4
   447→      } catch (err) {
   448→        console.error('[SPLICE] Error creating protected marker:', err);
   449→        results.errors++;
   450→      }
   451→    }
   452→
   453→    // Blue markers for takes (speech segments)
   454→    for (const take of takes) {
   455→      try {
   456→        const start = take.startTime || take.start;
   457→        const end = take.endTime || take.end;
   458→        const duration = end - start;
   459→        const textPreview = take.text?.substring(0, 30) || 'Speech';
   460→
   461→        // Use TickTime API directly (no TICKS_PER_SECOND needed)
   462→        const startTime = ppro.TickTime.createWithSeconds(start);
   463→        const durationTime = ppro.TickTime.createWithSeconds(duration);
   464→
   465→        const action = markers.createAddMarkerAction(
   466→          `SPLICE: Take`,
   467→          ppro.Marker.MARKER_TYPE_COMMENT,
   468→          startTime,
   469→          durationTime,
   470→          `Speech segment: ${textPreview}${take.text?.length > 30 ? '...' : ''}`
   471→        );
   472→        markerActions.push({ action, colorIndex: 5, type: 'take' }); // Blue = index 5
   473→      } catch (err) {
   474→        console.error('[SPLICE] Error creating take marker:', err);
   475→        results.errors++;
   476→      }
   477→    }
   478→
   479→    // Execute all marker actions in a single transaction
   480→    if (markerActions.length > 0) {
   481→      await project.lockedAccess(async () => {
   482→        await project.executeTransaction((compoundAction) => {
   483→          for (const { action } of markerActions) {
   484→            compoundAction.addAction(action);
   485→          }
   486→        }, 'SPLICE: Add Timeline Markers');
   487→      });
   488→
   489→      results.added = markerActions.length;
   490→      console.log(`[SPLICE] Added ${results.added} timeline markers`);
   491→    }
   492→
   493→  } catch (err) {
   494→    console.error('[SPLICE] Timeline markers error:', err);
   495→    results.errors++;
   496→  }
   497→
   498→  return results;
   499→}
