   501→/**
   502→ * Read current SPLICE marker positions from timeline.
   503→ * This allows users to manually adjust markers before applying.
   504→ * Returns silences based on current marker positions (not original detection).
   505→ */
   506→async function readMarkersAssilences() {
   507→  const silences = [];
   508→
   509→  try {
   510→    const context = await getActiveSequence();
   511→    if (!context) return silences;
   512→
   513→    const { sequence } = context;
   514→    const markers = await ppro.Markers.getMarkers(sequence);
   515→    if (!markers) return silences;
   516→
   517→    const allMarkers = await markers.getMarkers();
   518→    if (!allMarkers || allMarkers.length === 0) return silences;
   519→
   520→    for (const marker of allMarkers) {
   521→      const name = await marker.getName();
   522→
   523→      // Only read "Remove" markers (red ones that will be cut)
   524→      if (name && name.startsWith('SPLICE: Remove')) {
   525→        const startTickTime = await marker.getStart();
   526→        const durationTickTime = await marker.getDuration();
   527→
   528→        // Use TickTime.seconds property to get seconds directly (no TICKS_PER_SECOND needed)
   529→        const start = startTickTime.seconds;
   530→        const duration = durationTickTime.seconds;
   531→        const end = start + duration;
   532→
   533→        silences.push({
   534→          start,
   535→          end,
   536→          duration,
   537→          fromMarker: true  // Flag to indicate this came from marker adjustment
   538→        });
   539→      }
   540→    }
   541→
   542→    // Sort by start time
   543→    silences.sort((a, b) => a.start - b.start);
   544→    console.log(`[SPLICE] Read ${silences.length} silences from adjusted markers`);
   545→
   546→  } catch (err) {
   547→    console.error('[SPLICE] Error reading markers:', err);
   548→  }
   549→
   550→  return silences;
   551→}
