   667→        <div class="preview-item-info">
   668→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   669→          <div class="preview-item-duration">${textPreview}${item.data.text?.length > 40 ? '...' : ''}</div>
   670→        </div>
   671→        <button class="preview-item-seek" data-time="${item.start}" title="Seek to take">&gt;</button>
