   943→async function applySelectedSilences() {
   944→  if (selectedSilenceIndices.size === 0) {
   945→    setStatus('No silences selected');
   946→    return;
   947→  }
   948→
   949→  showProgress('Reading marker positions...');
   950→
   951→  // Read current marker positions from timeline
   952→  // This allows users to manually adjust markers before applying
   953→  const markerSilences = await readMarkersAssilences();
   954→
   955→  let silencesToApply;
   956→  let adjustedCount = 0;
   957→
   958→  if (markerSilences.length > 0) {
   959→    // Use marker positions (may have been adjusted by user)
   960→    silencesToApply = markerSilences;
   961→    adjustedCount = markerSilences.length;
   962→    console.log(`[SPLICE] Using ${adjustedCount} silences from adjusted markers`);
   963→  } else {
   964→    // Fallback to original detection data
   965→    silencesToApply = safeSilences.filter((_, i) => selectedSilenceIndices.has(i));
   966→    console.log(`[SPLICE] Using ${silencesToApply.length} silences from original detection`);
   967→  }
   968→
   969→  if (silencesToApply.length === 0) {
   970→    setStatus('No silences to apply');
   971→    return;
