   984→
   985→  // Hide preview
   986→  if (ui.combinedPreview) ui.combinedPreview.style.display = 'none';
   987→
   988→  // Show results
   989→  showSilenceResults({
   990→    count: silencesToApply.length,
   991→    timeSaved: silencesToApply.reduce((sum, s) => sum + (s.end - s.start), 0),
   992→    clipsModified: result
   993→  });
   994→
   995→  // Show advanced section
   996→  if (ui.advancedSection) ui.advancedSection.classList.remove('hidden');
   997→
   998→  const adjustedInfo = adjustedCount > 0 ? ' (from adjusted markers)' : '';
   999→  setStatus(`Removed ${silencesToApply.length} silence(s) - ${result} clip(s) modified${adjustedInfo}`);
  1000→
  1001→  // Refresh credits (await to prevent race conditions)
  1002→  try {
  1003→    await refreshCredits();
  1004→  } catch (err) {
  1005→    console.warn('[SPLICE] Credits refresh failed:', err);
