  1153→
  1154→// 1. ROBUST CONSTANTS (Fixes 'undefined' error)
  1155→// We define these integers explicitly because UXP API versions vary on where they store Enums.
  1156→const ProjectItemType = {
  1157→    CLIP: 1,
  1158→    BIN: 2,
  1159→    ROOT: 3,
  1160→    FILE: 4
  1161→};
  1162→
  1163→/**
  1164→ * Find the first media item in the project bin (recursively)
  1165→ * @param {Object} item - ProjectItem (usually rootItem)
  1166→ * @returns {Promise<Object|null>} First media item found or null
  1167→ */
  1168→async function findFirstMediaItem(item) {
  1169→  if (!item) return null;
  1170→
  1171→  // Fix: Use local Constant instead of app.Constants.ProjectItemType
  1172→  if (item.type === ProjectItemType.CLIP) {
  1173→    return item;
  1174→  }
  1175→
  1176→  // Search Children (Bin or Root)
  1177→  // Note: item.children is a collection, check numItems
  1178→  if (item.children && item.children.numItems > 0) {
  1179→    for (let i = 0; i < item.children.numItems; i++) {
  1180→      const child = item.children[i];
  1181→      const found = await findFirstMediaItem(child);
  1182→      if (found) return found;
  1183→    }
  1184→  }
  1185→
  1186→  return null;
  1187→}
