  1189→/**
  1190→ * Build a sequence from detection data locally
  1191→ * Used for testing via console: await buildSequenceV35(silences)
  1192→ */
  1193→async function buildSequenceV35(silences, takes = []) {
  1194→    if (!window.spliceBuilder) {
  1195→        throw new Error('Builder not initialized. Check if builder.js is loaded.');
  1196→    }
  1197→
  1198→    const app = require('premierepro');
  1199→    const project = await app.Project.getActiveProject();
  1200→    if (!project) throw new Error("No active project found.");
  1201→
  1202→    const rootItem = await project.getRootItem();
  1203→    const sourceProjectItem = await findFirstMediaItem(rootItem);
  1204→
  1205→    if (!sourceProjectItem) {
  1206→        throw new Error('No media items found in project bin.');
  1207→    }
  1208→
  1209→    console.log(`[SPLICE] Found source media: ${sourceProjectItem.name}`);
  1210→
  1211→    return await window.spliceBuilder.buildSequenceFromDetection(silences, takes, sourceProjectItem);
  1212→}
