  1568→    // Get settings
  1569→    const sensitivity = parseInt(document.getElementById('sensitivitySlider')?.value || 50);
  1570→    const params = mapSensitivity(sensitivity);
  1571→
  1572→    // Call batch endpoint
  1573→    const response = await fetch(`${BACKEND_URL}/batch/silences`, {
  1574→      method: 'POST',
  1575→      headers: getAuthHeaders(),
  1576→      body: JSON.stringify({
  1577→        files: batchQueue,
  1578→        options: {
  1579→          sensitivity,
  1580→          threshold: params.dbThreshold,
  1581→          minSilenceLength: params.minDuration,
  1582→          paddingStart: params.padding,
  1583→          paddingEnd: params.padding / 2
  1584→        }
  1585→      })
  1586→    });
  1587→
  1588→    if (!response.ok) {
  1589→      const err = await response.json();
  1590→      throw new Error(err.error || 'Batch request failed');
  1591→    }
  1592→
  1593→    const data = await response.json();
  1594→    currentBatchJobId = data.jobId;
  1595→    setStatus(`Batch job started: ${data.jobId}`);
  1596→
  1597→    // Poll for status
  1598→    await pollBatchStatus(currentBatchJobId, batchProgressBar, batchProgressText);
  1599→
  1600→  } catch (err) {
  1601→    setStatus('Batch error: ' + err.message);
  1602→  } finally {
  1603→    if (batchProgress) batchProgress.style.display = 'none';
  1604→  }
  1605→}
  1606→
  1607→/**
  1608→ * Poll batch job status
  1609→ */
  1610→async function pollBatchStatus(jobId, progressBar, progressText) {
  1611→  const pollInterval = 2000; // 2 seconds
  1612→  const maxPolls = 300; // 10 minutes max
  1613→  let polls = 0;
  1614→
  1615→  while (polls < maxPolls) {
