    80→
    81→// Maximum file size for audio processing (500MB)
    82→const MAX_FILE_SIZE_BYTES = 500 * 1024 * 1024;
    83→
    84→/**
    85→ * Validate file size to prevent OOM crashes
    86→ * @param {string} filePath - Path to file
    87→ * @returns {Promise<{valid: boolean, size?: number, error?: string}>}
    88→ */
    89→async function validateFileSize(filePath) {
    90→  try {
    91→    const stats = await require('fs').promises.stat(filePath);
    92→    if (stats.size > MAX_FILE_SIZE_BYTES) {
    93→      return {
    94→        valid: false,
    95→        size: stats.size,
    96→        error: `File too large (${(stats.size / 1024 / 1024).toFixed(1)}MB). Maximum allowed: ${MAX_FILE_SIZE_BYTES / 1024 / 1024}MB`
    97→      };
    98→    }
    99→    return { valid: true, size: stats.size };
   100→  } catch (err) {
   101→    return { valid: false, error: `Cannot access file: ${err.message}` };
   102→  }
   103→}
