   131→// Helper to determine tier from price ID with logging
   132→function getTierFromPriceId(priceId) {
   133→  if (priceId === process.env.STRIPE_PRICE_STARTER) return 'starter';
   134→  if (priceId === process.env.STRIPE_PRICE_PRO) return 'pro';
   135→  if (priceId === process.env.STRIPE_PRICE_TEAM) return 'team';
   136→
   137→  // Log unknown price ID for debugging
   138→  console.warn(`[SPLICE] Unknown price ID: ${priceId} - defaulting to starter tier`);
   139→  return 'starter';
   140→}
