   142→// Stripe webhook needs raw body - must be before express.json()
   143→app.post('/webhooks/stripe', express.raw({ type: 'application/json' }), async (req, res) => {
   144→  const sig = req.headers['stripe-signature'];
   145→  const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;
   146→
   147→  let event;
   148→
   149→  try {
   150→    if (webhookSecret) {
   151→      event = stripe.webhooks.constructEvent(req.body, sig, webhookSecret);
   152→    } else if (isProduction) {
   153→      // SECURITY: Reject unsigned webhooks in production
   154→      console.error('[SPLICE] CRITICAL: STRIPE_WEBHOOK_SECRET not set in production');
   155→      return res.status(500).json({ error: 'Webhook configuration error: secret not configured' });
   156→    } else {
   157→      // For local development testing only
   158→      // req.body is a Buffer from express.raw(), convert to string for JSON.parse
   159→      const bodyString = typeof req.body === 'string' ? req.body : req.body.toString('utf8');
   160→      event = JSON.parse(bodyString);
   161→      console.warn('[SPLICE] Warning: Processing webhook without signature verification (dev only)');
   162→    }
   163→  } catch (err) {
   164→    console.error('[SPLICE] Webhook signature verification failed:', err.message);
   165→    return res.status(400).json({ error: 'Webhook signature verification failed' });
   166→  }
   167→
   168→  console.log(`[SPLICE] Webhook received: ${event.type} (${event.id})`);
   169→
   170→  // Idempotency check - skip if already processed
   171→  if (await usageTracking.isEventProcessed(event.id)) {
   172→    console.log(`[SPLICE] Event ${event.id} already processed, skipping`);
   173→    return res.json({ received: true, skipped: true });
   174→  }
   175→
   176→  try {
   177→    switch (event.type) {
   178→      case 'customer.subscription.created':
   179→      case 'customer.subscription.updated': {
   180→        const subscription = event.data.object;
   181→        const customerId = subscription.customer;
   182→
   183→        // Validate customerId
   184→        if (!customerId) {
   185→          console.error('[SPLICE] Missing customer ID in subscription event');
   186→          return res.status(400).json({ error: 'Missing customer ID' });
   187→        }
   188→
   189→        // Get tier from price ID
   190→        const priceId = subscription.items?.data?.[0]?.price?.id;
   191→        const tier = getTierFromPriceId(priceId);
   192→
   193→        // Update user tier and reset hours
   194→        await usageTracking.updateTier(customerId, tier);
   195→        console.log(`[SPLICE] Updated customer ${customerId} to tier: ${tier}`);
   196→
   197→        // Generate license key for new subscriptions with retry and delivery
   198→        if (event.type === 'customer.subscription.created') {
   199→          let licenseResult = null;
   200→          let retryCount = 0;
   201→          const maxRetries = 3;
   202→
   203→          // Retry mechanism for license key generation
   204→          while (retryCount < maxRetries) {
   205→            licenseResult = await licenseService.generateLicenseKey(customerId);
   206→            if (licenseResult.success) {
   207→              break;
   208→            }
   209→            retryCount++;
   210→            console.warn(`[SPLICE] License key generation attempt ${retryCount}/${maxRetries} failed: ${licenseResult.error}`);
   211→            // Wait before retry (exponential backoff)
   212→            if (retryCount < maxRetries) {
   213→              await new Promise(resolve => setTimeout(resolve, 1000 * retryCount));
   214→            }
   215→          }
   216→
   217→          if (licenseResult && licenseResult.success) {
   218→            console.log(`[SPLICE] Generated license key for ${customerId}: ${licenseResult.key}`);
   219→
   220→            // Store license key in Stripe subscription metadata as backup
   221→            try {
   222→              await stripe.subscriptions.update(subscription.id, {
   223→                metadata: {
   224→                  license_key: licenseResult.key,
   225→                  license_generated_at: new Date().toISOString()
   226→                }
   227→              });
   228→              console.log(`[SPLICE] Stored license key in Stripe metadata for subscription ${subscription.id}`);
   229→            } catch (metaErr) {
   230→              console.error(`[SPLICE] Failed to store license key in Stripe metadata:`, metaErr.message);
   231→            }
   232→
   233→            // Get customer email and send license key
   234→            try {
   235→              const customer = await stripe.customers.retrieve(customerId);
   236→              if (customer.email) {
   237→                // Log email delivery (placeholder for actual email service)
   238→                console.log(`[SPLICE] License key ready for delivery to ${customer.email}: ${licenseResult.key}`);
   239→                // TODO: Integrate with email service (SendGrid, SES, etc.)
   240→                // await sendLicenseKeyEmail(customer.email, licenseResult.key, tier);
   241→
   242→                // Store email in database for reference
   243→                await usageTracking.updateTier(customerId, tier, customer.email);
   244→              } else {
   245→                console.warn(`[SPLICE] No email found for customer ${customerId}`);
   246→              }
   247→            } catch (emailErr) {
   248→              console.error(`[SPLICE] Error getting customer email:`, emailErr.message);
   249→            }
   250→          } else {
   251→            console.error(`[SPLICE] Failed to generate license key after ${maxRetries} attempts: ${licenseResult?.error || 'Unknown error'}`);
   252→          }
   253→        }
   254→        break;
   255→      }
   256→
   257→      case 'customer.subscription.deleted': {
   258→        const subscription = event.data.object;
   259→        const customerId = subscription.customer;
   260→
   261→        // Validate customerId
   262→        if (!customerId) {
   263→          console.error('[SPLICE] Missing customer ID in subscription.deleted event');
   264→          return res.status(400).json({ error: 'Missing customer ID' });
   265→        }
   266→
   267→        // Downgrade to cancelled (0 hours)
   268→        await usageTracking.updateTier(customerId, 'cancelled');
   269→        console.log(`[SPLICE] Subscription cancelled for customer ${customerId}`);
   270→        break;
   271→      }
   272→
   273→      case 'invoice.payment_succeeded': {
   274→        const invoice = event.data.object;
   275→        const customerId = invoice.customer;
   276→        const subscriptionId = invoice.subscription;
   277→
   278→        // Validate customerId
   279→        if (!customerId) {
   280→          console.error('[SPLICE] Missing customer ID in invoice event');
   281→          return res.status(400).json({ error: 'Missing customer ID' });
   282→        }
   283→
   284→        // Reset hours on successful payment (new billing period)
   285→        if (subscriptionId) {
   286→          const subscription = await stripe.subscriptions.retrieve(subscriptionId);
   287→          const priceId = subscription.items?.data?.[0]?.price?.id;
   288→          const tier = getTierFromPriceId(priceId);
   289→
   290→          await usageTracking.resetHours(customerId, tier);
   291→          console.log(`[SPLICE] Reset hours for customer ${customerId} (tier: ${tier})`);
   292→        }
   293→        break;
   294→      }
   295→
   296→      default:
   297→        console.log(`[SPLICE] Unhandled event type: ${event.type}`);
   298→    }
   299→
   300→    // Record event as processed (idempotency)
   301→    await usageTracking.recordWebhookEvent(event.id, event.type);
   302→
   303→    res.json({ received: true });
   304→  } catch (err) {
   305→    console.error('[SPLICE] Webhook handler error:', err);
   306→    res.status(500).json({ error: err.message });
   307→  }
   308→});
