   376→/**
   377→ * POST /analyze - Main analysis endpoint
   378→ *
   379→ * Pipeline:
   380→ * 1. Validate input (wavPath)
   381→ * 2. Slice 4: Transcribe audio with Whisper
   382→ * 3. Slice 5: Detect takes with GPT-4o-mini
   383→ * 4. Return combined results
   384→ */
   385→app.post('/analyze', requireCredits({ endpoint: 'analyze' }), async (req, res) => {
   386→  const { wavPath } = req.body;
   387→
   388→  // Validate input
   389→  if (!wavPath) {
   390→    return res.status(400).json({ error: 'wavPath is required' });
   391→  }
   392→
   393→  if (!(await fileExists(wavPath))) {
   394→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   395→  }
   396→
   397→  console.log(`[SPLICE] Analyzing: ${wavPath}`);
   398→
   399→  try {
   400→    // Slice 4 - GPT-4o-mini transcription
   401→    const transcript = await transcribeAudio(wavPath);
   402→
   403→    // Slice 5 - GPT-4o-mini take detection
   404→    const takes = await detectTakes(transcript);
   405→
   406→    // Deduct usage based on audio duration
   407→    const audioDuration = transcript.duration || 0;
   408→    let balance = null;
   409→    if (audioDuration > 0 && req.deductUsage) {
   410→      balance = await req.deductUsage(audioDuration);
   411→    }
   412→
   413→    res.json({
   414→      success: true,
   415→      wavPath,
   416→      transcript,
   417→      takes,
   418→      balance: balance ? { hoursRemaining: balance.hoursRemaining, tier: balance.tier } : undefined
   419→    });
   420→  } catch (err) {
   421→    console.error('[SPLICE] Error:', err);
   422→    res.status(500).json({ error: err.message });
   423→  }
   424→});
