   426→/**
   427→ * POST /silences - Detect silent gaps in audio
   428→ *
   429→ * Pipeline:
   430→ * 1. Transcribe audio with Whisper (cached)
   431→ * 2. Analyze gaps between segments
   432→ * 3. Return silence regions
   433→ */
   434→app.post('/silences', requireCredits({ endpoint: 'silences' }), async (req, res) => {
   435→  const { wavPath, threshold = 0.5 } = req.body;
   436→
   437→  if (!wavPath) {
   438→    return res.status(400).json({ error: 'wavPath is required' });
   439→  }
   440→
   441→  if (!(await fileExists(wavPath))) {
   442→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   443→  }
   444→
   445→  console.log(`[SPLICE] Detecting silences: ${wavPath} (threshold: ${threshold}s)`);
   446→
   447→  try {
   448→    const transcript = await transcribeAudio(wavPath);
   449→    const silences = detectSilences(transcript.segments, threshold);
   450→
   451→    // Deduct usage based on audio duration
   452→    const audioDuration = transcript.duration || 0;
   453→    let balance = null;
   454→    if (audioDuration > 0 && req.deductUsage) {
   455→      balance = await req.deductUsage(audioDuration);
   456→    }
   457→
   458→    res.json({
   459→      success: true,
   460→      wavPath,
   461→      threshold,
   462→      silences,
   463→      count: silences.length,
   464→      totalSilenceDuration: silences.reduce((sum, s) => sum + s.duration, 0).toFixed(2),
   465→      balance: balance ? { hoursRemaining: balance.hoursRemaining, tier: balance.tier } : undefined
   466→    });
   467→  } catch (err) {
   468→    console.error('[SPLICE] Silence detection error:', err);
   469→    res.status(500).json({ error: err.message });
   470→  }
   471→});
