   473→/**
   474→ * POST /silences-audio - Detect silences using FFprobe audio analysis
   475→ *
   476→ * Uses actual audio levels (dB threshold) instead of transcript gaps.
   477→ * More accurate for detecting silence vs background noise.
   478→ */
   479→app.post('/silences-audio', requireCredits({ endpoint: 'silences-audio' }), async (req, res) => {
   480→  const {
   481→    wavPath,
   482→    threshold = -30,
   483→    minDuration = 0.5,
   484→    padding = 0.1
   485→  } = req.body;
   486→
   487→  if (!wavPath) {
   488→    return res.status(400).json({ error: 'wavPath is required' });
   489→  }
   490→
   491→  if (!(await fileExists(wavPath))) {
   492→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   493→  }
   494→
   495→  // Check FFprobe availability
   496→  const ffprobeAvailable = await isFFprobeInstalled();
   497→  if (!ffprobeAvailable) {
   498→    return res.status(500).json({
   499→      error: 'FFprobe not installed. Run: brew install ffmpeg'
   500→    });
   501→  }
   502→
   503→  console.log(`[SPLICE] FFprobe silence detection: ${wavPath} (threshold: ${threshold}dB, min: ${minDuration}s)`);
   504→
   505→  try {
   506→    const silences = await detectAudioSilences(wavPath, {
   507→      threshold,
   508→      minDuration,
   509→      padding
   510→    });
   511→
   512→    const totalDuration = silences.reduce((sum, s) => sum + s.duration, 0);
   513→
   514→    // Deduct usage based on audio duration
   515→    let balance = null;
   516→    try {
   517→      const audioDuration = await getAudioDuration(wavPath);
   518→      if (audioDuration > 0 && req.deductUsage) {
   519→        balance = await req.deductUsage(audioDuration);
   520→      }
   521→    } catch (durErr) {
   522→      console.warn('[SPLICE] Could not get audio duration for billing:', durErr.message);
   523→    }
   524→
   525→    res.json({
   526→      success: true,
   527→      wavPath,
   528→      threshold,
   529→      minDuration,
   530→      padding,
   531→      silences,
   532→      count: silences.length,
   533→      totalSilenceDuration: totalDuration.toFixed(2),
   534→      balance: balance ? { hoursRemaining: balance.hoursRemaining, tier: balance.tier } : undefined
   535→    });
   536→  } catch (err) {
   537→    console.error('[SPLICE] FFprobe silence detection error:', err);
   538→    res.status(500).json({ error: err.message });
   539→  }
   540→});
