   838→/**
   839→ * POST /fillers - Detect filler words (um, uh, like, etc.)
   840→ *
   841→ * Transcribes audio and identifies filler words with timestamps.
   842→ * Returns segments that can be cut or reviewed for removal.
   843→ *
   844→ * Options:
   845→ * - wavPath: Path to audio file (required unless transcript provided)
   846→ * - transcript: Pre-existing transcript with word-level timing (optional)
   847→ * - customFillers: Additional filler words to detect (optional)
   848→ */
   849→app.post('/fillers', requireCredits({ endpoint: 'fillers' }), async (req, res) => {
   850→  const {
   851→    wavPath,
   852→    transcript: providedTranscript,
   853→    customFillers = []
   854→  } = req.body;
   855→
   856→  if (!wavPath && !providedTranscript) {
   857→    return res.status(400).json({ error: 'wavPath or transcript is required' });
   858→  }
   859→
   860→  if (wavPath && !(await fileExists(wavPath))) {
   861→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   862→  }
   863→
   864→  console.log(`[SPLICE] Filler word detection: ${wavPath || 'provided transcript'}`);
   865→
   866→  try {
   867→    // Get or create transcript with word-level timestamps
   868→    let transcript = providedTranscript;
   869→    if (!transcript && wavPath) {
   870→      transcript = await transcribeWithWords(wavPath);
   871→    }
   872→
   873→    // Validate transcript has words
   874→    if (!transcript || !transcript.words || transcript.words.length === 0) {
   875→      return res.status(400).json({
   876→        error: 'Transcript must contain word-level timing data'
   877→      });
   878→    }
   879→
   880→    // Default filler words (common in English speech)
   881→    const defaultFillers = [
   882→      'um', 'uh', 'ah', 'er', 'eh',           // Hesitation sounds
   883→      'like', 'so', 'well', 'right',           // Discourse markers
   884→      'you know', 'i mean', 'basically',       // Filler phrases
   885→      'actually', 'literally', 'honestly',     // Overused qualifiers
   886→      'kind of', 'sort of', 'you see'          // Hedging phrases
   887→    ];
   888→
   889→    // Combine default + custom fillers (lowercase for matching)
   890→    const fillerSet = new Set([
   891→      ...defaultFillers,
   892→      ...customFillers.map(f => f.toLowerCase().trim())
   893→    ]);
   894→
   895→    // Detect filler words
   896→    const fillers = [];
   897→    const words = transcript.words;
   898→
   899→    for (let i = 0; i < words.length; i++) {
   900→      const word = words[i];
   901→      const normalizedWord = word.word.toLowerCase().replace(/[.,!?;:'"]/g, '').trim();
   902→
   903→      // Check single-word fillers
   904→      if (fillerSet.has(normalizedWord)) {
   905→        fillers.push({
   906→          word: word.word,
   907→          normalizedWord,
   908→          start: word.start,
   909→          end: word.end,
   910→          duration: word.end - word.start,
   911→          index: i,
   912→          type: 'filler'
   913→        });
   914→        continue;
   915→      }
   916→
   917→      // Check two-word phrases (e.g., "you know", "kind of")
   918→      if (i < words.length - 1) {
   919→        const nextWord = words[i + 1];
   920→        const twoWordPhrase = `${normalizedWord} ${nextWord.word.toLowerCase().replace(/[.,!?;:'"]/g, '').trim()}`;
   921→        if (fillerSet.has(twoWordPhrase)) {
   922→          fillers.push({
   923→            word: `${word.word} ${nextWord.word}`,
   924→            normalizedWord: twoWordPhrase,
   925→            start: word.start,
   926→            end: nextWord.end,
   927→            duration: nextWord.end - word.start,
   928→            index: i,
   929→            type: 'filler_phrase'
   930→          });
   931→          // Skip next word since it's part of this phrase
   932→          i++;
   933→        }
   934→      }
   935→    }
   936→
   937→    // Calculate total filler time
   938→    const totalFillerDuration = fillers.reduce((sum, f) => sum + f.duration, 0);
   939→    const audioDuration = transcript.duration || (words.length > 0 ? words[words.length - 1].end : 0);
   940→    const fillerPercentage = audioDuration > 0 ? (totalFillerDuration / audioDuration) * 100 : 0;
   941→
   942→    // Deduct usage based on audio duration
   943→    let balance = null;
   944→    if (audioDuration > 0 && req.deductUsage) {
   945→      balance = await req.deductUsage(audioDuration);
   946→    }
   947→
   948→    res.json({
   949→      success: true,
   950→      wavPath,
   951→      fillers,
   952→      metadata: {
   953→        totalWords: words.length,
   954→        fillerCount: fillers.length,
   955→        totalFillerDuration: parseFloat(totalFillerDuration.toFixed(3)),
   956→        audioDuration: parseFloat(audioDuration.toFixed(3)),
   957→        fillerPercentage: parseFloat(fillerPercentage.toFixed(2)),
   958→        fillersPerMinute: audioDuration > 0 ? parseFloat((fillers.length / (audioDuration / 60)).toFixed(2)) : 0
   959→      },
   960→      removalSegments: fillers.map(f => ({
   961→        start: f.start,
   962→        end: f.end,
   963→        duration: f.duration,
   964→        reason: `Filler: "${f.word}"`,
   965→        type: f.type
   966→      })),
   967→      balance: balance ? { hoursRemaining: balance.hoursRemaining, tier: balance.tier } : undefined
   968→    });
   969→  } catch (err) {
   970→    console.error('[SPLICE] Filler detection error:', err);
   971→    res.status(500).json({ error: err.message });
   972→  }
   973→});
