  1058→/**
  1059→ * POST /export/captions - Export transcript to caption format (SRT, VTT, etc.)
  1060→ *
  1061→ * Converts a transcript to the specified caption format.
  1062→ * Can optionally save to file.
  1063→ *
  1064→ * Options:
  1065→ * - wavPath: Path to audio file (to transcribe first)
  1066→ * - transcript: Pre-existing transcript with word-level timing
  1067→ * - format: Export format (srt, vtt, txt, json) - default: srt
  1068→ * - outputPath: Optional file path to save to
  1069→ * - maxWordsPerCaption: Max words per caption (default: 8)
  1070→ * - maxDuration: Max duration per caption in seconds (default: 5)
  1071→ */
  1072→app.post('/export/captions', requireCredits({ endpoint: 'export-captions' }), async (req, res) => {
  1073→  const {
  1074→    wavPath,
  1075→    transcript: providedTranscript,
  1076→    format = 'srt',
  1077→    outputPath = null,
  1078→    maxWordsPerCaption = 8,
  1079→    maxDuration = 5
  1080→  } = req.body;
  1081→
  1082→  if (!wavPath && !providedTranscript) {
  1083→    return res.status(400).json({ error: 'wavPath or transcript is required' });
  1084→  }
  1085→
  1086→  if (wavPath && !(await fileExists(wavPath))) {
  1087→    return res.status(404).json({ error: `File not found: ${wavPath}` });
  1088→  }
  1089→
  1090→  console.log(`[SPLICE] Caption export: ${wavPath || 'provided transcript'} -> ${format}`);
  1091→
  1092→  try {
  1093→    // Get or create transcript with word-level timestamps
  1094→    let transcript = providedTranscript;
  1095→    if (!transcript && wavPath) {
  1096→      transcript = await transcribeWithWords(wavPath);
  1097→    }
  1098→
  1099→    const exportOptions = { maxWordsPerCaption, maxDuration };
  1100→
  1101→    // Generate caption content based on format
  1102→    let content;
  1103→    let mimeType;
  1104→
  1105→    switch (format.toLowerCase()) {
  1106→      case 'srt':
  1107→        content = toSRT(transcript, exportOptions);
  1108→        mimeType = 'application/x-subrip';
  1109→        break;
  1110→      case 'vtt':
  1111→      case 'webvtt':
  1112→        content = toVTT(transcript, exportOptions);
  1113→        mimeType = 'text/vtt';
  1114→        break;
  1115→      case 'txt':
  1116→      case 'text':
  1117→        content = toPlainText(transcript, { ...exportOptions, includeTimestamps: true });
  1118→        mimeType = 'text/plain';
  1119→        break;
  1120→      case 'json':
  1121→        content = toJSON(transcript);
  1122→        mimeType = 'application/json';
  1123→        break;
  1124→      default:
  1125→        return res.status(400).json({
  1126→          error: `Unsupported format: ${format}`,
  1127→          supportedFormats: getSupportedFormats()
  1128→        });
  1129→    }
