  1796→// In-memory job queue for batch processing
  1797→const batchJobs = new Map();
  1798→
  1799→// Batch job limits to prevent memory leak
  1800→const MAX_BATCH_JOBS = 10000;
  1801→const BATCH_JOB_MAX_AGE_MS = 24 * 60 * 60 * 1000; // 24 hours
  1802→
  1803→/**
  1804→ * Generate a unique job ID
  1805→ */
  1806→function generateJobId() {
  1807→  return `batch_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  1808→}
  1809→
  1810→/**
  1811→ * Clean up old batch jobs to prevent memory leak
  1812→ * Removes jobs older than 24 hours
  1813→ */
  1814→function cleanupOldBatchJobs() {
  1815→  const now = Date.now();
  1816→  let removedCount = 0;
  1817→
  1818→  for (const [jobId, job] of batchJobs.entries()) {
  1819→    const createdAt = new Date(job.createdAt).getTime();
  1820→    if (now - createdAt > BATCH_JOB_MAX_AGE_MS) {
  1821→      batchJobs.delete(jobId);
  1822→      removedCount++;
  1823→    }
  1824→  }
  1825→
  1826→  if (removedCount > 0) {
  1827→    console.log(`[SPLICE] Cleaned up ${removedCount} old batch job(s)`);
  1828→  }
  1829→}
  1830→
  1831→/**
  1832→ * Enforce max job limit by removing oldest completed jobs
  1833→ */
  1834→function enforceJobLimit() {
  1835→  if (batchJobs.size < MAX_BATCH_JOBS) return;
  1836→
  1837→  // Get completed jobs sorted by creation date (oldest first)
  1838→  const completedJobs = Array.from(batchJobs.entries())
  1839→    .filter(([_, job]) => job.status !== 'processing')
  1840→    .sort((a, b) => new Date(a[1].createdAt) - new Date(b[1].createdAt));
  1841→
  1842→  // Remove oldest completed jobs until under limit
  1843→  const toRemove = batchJobs.size - MAX_BATCH_JOBS + 1;
  1844→  for (let i = 0; i < Math.min(toRemove, completedJobs.length); i++) {
  1845→    batchJobs.delete(completedJobs[i][0]);
  1846→  }
  1847→
  1848→  console.log(`[SPLICE] Enforced job limit, removed ${Math.min(toRemove, completedJobs.length)} job(s)`);
  1849→}
  1850→
  1851→// Run cleanup every hour
  1852→setInterval(cleanupOldBatchJobs, 60 * 60 * 1000);
  1853→
  1854→/**
  1855→ * POST /batch/silences - Process multiple files for silence detection
  1856→ *
  1857→ * Creates a batch job that processes multiple audio files.
  1858→ * Returns a job ID for tracking progress.
  1859→ *
  1860→ * Body:
  1861→ * - files: Array of file paths to process
  1862→ * - options: Detection options (sensitivity, threshold, etc.)
  1863→ */
  1864→app.post('/batch/silences', requireCredits({ endpoint: 'batch-silences' }), async (req, res) => {
  1865→  const { files, options = {} } = req.body;
  1866→
  1867→  if (!files || !Array.isArray(files) || files.length === 0) {
  1868→    return res.status(400).json({ error: 'files array is required' });
  1869→  }
  1870→
  1871→  // Validate all files exist
  1872→  const missingFiles = files.filter(f => !fs.existsSync(f));
  1873→  if (missingFiles.length > 0) {
  1874→    return res.status(404).json({
  1875→      error: 'Some files not found',
  1876→      missingFiles
  1877→    });
  1878→  }
  1879→
  1880→  // Enforce job limit before creating new job
  1881→  enforceJobLimit();
  1882→
  1883→  const jobId = generateJobId();
  1884→
  1885→  // Initialize job with customer ID for usage tracking
  1886→  const job = {
  1887→    id: jobId,
  1888→    type: 'silences',
  1889→    status: 'processing',
  1890→    createdAt: new Date().toISOString(),
  1891→    stripeCustomerId: req.stripeCustomerId,  // Store for usage deduction
  1892→    files: files.map(f => ({
  1893→      path: f,
  1894→      status: 'pending',
  1895→      result: null,
  1896→      error: null
  1897→    })),
  1898→    options,
  1899→    progress: {
  1900→      total: files.length,
  1901→      completed: 0,
  1902→      failed: 0,
  1903→      percentage: 0
  1904→    },
  1905→    results: [],
  1906→    errors: [],
  1907→    totalUsageDeducted: 0  // Track total seconds deducted
  1908→  };
  1909→
  1910→  batchJobs.set(jobId, job);
  1911→  console.log(`[SPLICE] Batch job ${jobId} created with ${files.length} files`);
  1912→
  1913→  // Start processing in background
  1914→  processBatchJob(jobId);
  1915→
  1916→  res.json({
  1917→    success: true,
  1918→    jobId,
  1919→    message: `Batch job created with ${files.length} files`,
  1920→    statusUrl: `/batch/status/${jobId}`
  1921→  });
  1922→});
  1923→
  1924→/**
  1925→ * Process a batch job (runs in background)
  1926→ */
  1927→async function processBatchJob(jobId) {
  1928→  const job = batchJobs.get(jobId);
  1929→  if (!job) return;
  1930→
  1931→  const { sensitivity, ...manualOptions } = job.options;
  1932→
  1933→  // Build detection options
  1934→  let detectionOptions = {};
  1935→  if (typeof sensitivity === 'number') {
  1936→    detectionOptions = sensitivityToParams(sensitivity);
  1937→  } else {
  1938→    detectionOptions = {
  1939→      threshold: manualOptions.threshold ?? -30,
  1940→      minSilenceLength: manualOptions.minSilenceLength ?? 0.5,
  1941→      paddingStart: manualOptions.paddingStart ?? 0.1,
  1942→      paddingEnd: manualOptions.paddingEnd ?? 0.05,
  1943→      autoThreshold: manualOptions.autoThreshold ?? false
  1944→    };
  1945→  }
  1946→
  1947→  // Process files sequentially to avoid overwhelming the system
  1948→  for (let i = 0; i < job.files.length; i++) {
  1949→    const fileEntry = job.files[i];
  1950→    fileEntry.status = 'processing';
  1951→
  1952→    try {
  1953→      const result = await detectSilencesRMS(fileEntry.path, detectionOptions);
  1954→
  1955→      fileEntry.status = 'completed';
  1956→      fileEntry.result = {
  1957→        silences: result.silences,
  1958→        count: result.silences.length,
  1959→        totalSilenceDuration: result.metadata.totalSilenceDuration,
  1960→        audioDuration: result.metadata.audioDuration
  1961→      };
  1962→
  1963→      job.results.push({
  1964→        file: fileEntry.path,
  1965→        ...fileEntry.result
  1966→      });
  1967→
  1968→      // Deduct usage for this file
  1969→      const audioDuration = result.metadata?.audioDuration || 0;
  1970→      if (audioDuration > 0 && job.stripeCustomerId) {
  1971→        try {
  1972→          await usageTracking.deductUsage(job.stripeCustomerId, audioDuration, 'batch-silences');
  1973→          job.totalUsageDeducted += audioDuration;
  1974→        } catch (usageErr) {
  1975→          console.warn(`[SPLICE] Batch ${jobId}: Usage deduction failed:`, usageErr.message);
  1976→        }
  1977→      }
  1978→
  1979→      job.progress.completed++;
  1980→      console.log(`[SPLICE] Batch ${jobId}: ${i + 1}/${job.files.length} completed`);
  1981→    } catch (err) {
  1982→      fileEntry.status = 'failed';
  1983→      fileEntry.error = err.message;
  1984→
  1985→      job.errors.push({
  1986→        file: fileEntry.path,
  1987→        error: err.message
  1988→      });
  1989→
  1990→      job.progress.failed++;
  1991→      console.error(`[SPLICE] Batch ${jobId}: ${fileEntry.path} failed:`, err.message);
  1992→    }
  1993→
  1994→    // Update progress
  1995→    job.progress.percentage = Math.round(
  1996→      ((job.progress.completed + job.progress.failed) / job.progress.total) * 100
  1997→    );
  1998→  }
  1999→
  2000→  // Mark job as complete
  2001→  job.status = job.progress.failed === job.progress.total ? 'failed' :
  2002→               job.progress.failed > 0 ? 'completed_with_errors' : 'completed';
  2003→  job.completedAt = new Date().toISOString();
  2004→
  2005→  console.log(`[SPLICE] Batch job ${jobId} ${job.status}`);
  2006→}
