 * Evicts oldest entries when cache exceeds MAX_CACHE_SIZE
 * @param {string} key - Cache key
 * @param {any} value - Value to cache (should include mtime, result)
 */
function cacheSet(key, value) {
  // Clean up expired entries first
  cleanExpiredEntries();

  // Evict oldest entries if at capacity
  while (transcriptCache.size >= MAX_CACHE_SIZE) {
    const oldestKey = transcriptCache.keys().next().value;
    transcriptCache.delete(oldestKey);
    console.log(`[SPLICE] Cache eviction: removed ${oldestKey}`);
  }

  // Add cachedAt timestamp
  transcriptCache.set(key, {
    ...value,
    cachedAt: Date.now()
  });
}

/**
 * Get from cache with TTL check
 * @param {string} key - Cache key
 * @returns {object|null} Cached value or null if expired/missing
 */
function cacheGet(key) {
  const entry = transcriptCache.get(key);
  if (!entry) return null;

  // Check TTL
  if (Date.now() - entry.cachedAt > CACHE_TTL_MS) {
    transcriptCache.delete(key);
    console.log(`[SPLICE] Cache entry expired: ${key}`);
    return null;
  }

  return entry;
}

/**
 * Clean up expired cache entries
 * Called periodically and before cache set
 */
function cleanExpiredEntries() {
  const now = Date.now();
  let cleaned = 0;

  for (const [key, entry] of transcriptCache.entries()) {
    if (now - entry.cachedAt > CACHE_TTL_MS) {
      transcriptCache.delete(key);
      cleaned++;
    }
  }

  if (cleaned > 0) {
    console.log(`[SPLICE] Cleaned ${cleaned} expired cache entries`);
  }
}

/**
 * Unified transcription: calls Whisper ONCE with both word and segment granularities.
