  // Build unified result with both segments and words
  const result = {
    text: transcription.text,
    segments: (transcription.segments || []).map(s => ({
      id: s.id,
      start: s.start,
      end: s.end,
      text: s.text
    })),
    words: (transcription.words || []).map(w => ({
      word: w.word,
      start: w.start,
      end: w.end
    })),
    language: transcription.language || 'en',
    duration: transcription.duration || 0
  };

  // Cache the unified result
  cacheSet(cacheKey, { mtime, result });
  console.log(`[SPLICE] Full transcription cached (${result.segments.length} segments, ${result.words.length} words)`);

  return result;
}

/**
 * Transcribe audio file using Whisper with segment timestamps (with caching)
 * Uses unified transcribeFull() cache to avoid duplicate API calls.
 *
 * @param {string} wavPath - Path to the WAV file
 * @returns {Promise<{text: string, segments: Array, language: string, duration: number}>}
 */
async function transcribeAudio(wavPath) {
  // Use unified transcription (gets both segments and words)
  const full = await transcribeFull(wavPath);

  // Return segment-focused result (matches legacy format)
  return {
    text: full.text,
    segments: full.segments,
    language: full.language,
    duration: full.duration
  };
}

/**
 * Transcribe audio with word-level timestamps using Whisper
 * Uses unified transcribeFull() cache to avoid duplicate API calls.
 *
 * @param {string} wavPath - Path to the WAV file
 * @returns {Promise<{text: string, words: Array<{word: string, start: number, end: number}>}>}
 */
async function transcribeWithWords(wavPath) {
  // Use unified transcription (gets both segments and words)
  const full = await transcribeFull(wavPath);

  // Return word-focused result (matches legacy format)
  return {
    text: full.text,
    words: full.words,
    language: full.language,
    duration: full.duration
  };
}

/**
 * Clear transcription cache
 * Useful for testing or memory management
 */
function clearCache() {
  const size = transcriptCache.size;
  transcriptCache.clear();
  console.log(`[SPLICE] Cleared ${size} cached transcription(s)`);
  return size;
}

/**
 * Get cache statistics
 */
function getCacheStats() {
  const now = Date.now();
  const entries = [];

  for (const [key, entry] of transcriptCache.entries()) {
    const ageMs = now - entry.cachedAt;
    entries.push({
      key,
      ageMs,
      ageMinutes: Math.floor(ageMs / 60000),
      expiresInMs: Math.max(0, CACHE_TTL_MS - ageMs),
      expired: ageMs > CACHE_TTL_MS
    });
  }

  return {
    entries: transcriptCache.size,
    ttlMs: CACHE_TTL_MS,
    ttlMinutes: CACHE_TTL_MS / 60000,
    maxSize: MAX_CACHE_SIZE,
    keys: Array.from(transcriptCache.keys()),
    details: entries
  };
}

module.exports = { transcribeFull, transcribeAudio, transcribeWithWords, clearCache, getCacheStats };

