     1→/**
     2→ * Repetition/Stutter Detection Service
     3→ *
     4→ * Detects repeated words, phrases, and stutters in transcripts.
     5→ * Based on Fireside's proven approach with enhancements.
     6→ *
     7→ * Features:
     8→ * - Frequency-based pattern detection
     9→ * - Phrase similarity matching
    10→ * - OpenAI refinement for boundary detection
    11→ * - Configurable tolerance and phrase size
    12→ * - Word-level result with grouping
    13→ *
    14→ * Two Modes:
    15→ * - Basic: Fast local detection (no API calls)
    16→ * - Advanced: Uses OpenAI for precise boundary detection
    17→ */
    18→
    19→const OpenAI = require('openai');
    20→
    21→// =============================================================================
    22→// Configuration
    23→// =============================================================================
    24→
    25→const DEFAULT_OPTIONS = {
    26→  mode: 'basic',            // 'basic' or 'advanced'
    27→  phraseSize: 5,            // Words per comparison window
    28→  tolerance: 0.7,           // Similarity threshold (0-1)
    29→  searchRadius: 100,        // Words to search ahead
    30→  minOccurrences: 2,        // Minimum occurrences to count as repetition
    31→  extendMode: 'next-punctuation', // 'next-punctuation' or 'longest-occurrence'
    32→  useOpenAI: false,         // Use OpenAI for boundary refinement
    33→  maxGapWords: 20           // Max words between occurrences to group
    34→};
    35→
    36→// Note: Punctuation pattern available for future boundary detection enhancements
    37→// const PUNCTUATION = /[.!?,;:]/;
    38→
    39→// =============================================================================
    40→// Core Detection - Basic Mode
    41→// =============================================================================
    42→
    43→/**
    44→ * Detect repetitions in transcript using basic frequency analysis
    45→ *
    46→ * @param {Object} transcript - Transcript with words array
    47→ * @param {Object} options - Detection options
    48→ * @returns {Object} Detection results with repetitions
    49→ */
    50→function detectRepetitionsBasic(transcript, options = {}) {
    51→  const opts = { ...DEFAULT_OPTIONS, ...options };
    52→  const words = transcript.words || [];
    53→
    54→  if (words.length < opts.phraseSize * 2) {
    55→    return { repetitions: [], metadata: { reason: 'Not enough words for analysis' } };
    56→  }
    57→
    58→  console.log(`[SPLICE Repetition] Basic mode: ${words.length} words, phraseSize=${opts.phraseSize}, tolerance=${opts.tolerance}`);
    59→
    60→  // Normalize words for comparison
    61→  const normalizedWords = words.map(w => normalizeWord(w.word || w.text || ''));
    62→
    63→  // Find all phrase occurrences
    64→  const phraseGroups = findPhraseOccurrences(normalizedWords, {
    65→    phraseSize: opts.phraseSize,
    66→    searchRadius: opts.searchRadius,
    67→    tolerance: opts.tolerance,
    68→    minOccurrences: opts.minOccurrences
    69→  });
    70→
    71→  // Convert to time-based repetitions
    72→  const repetitions = phraseGroups.map((group, index) => ({
    73→    id: index,
    74→    occurrences: group.occurrences.map(occ => ({
    75→      startWord: occ.startIndex,
    76→      endWord: occ.endIndex,
    77→      start: words[occ.startIndex].start,
    78→      end: words[occ.endIndex].end,
    79→      text: words.slice(occ.startIndex, occ.endIndex + 1).map(w => w.word || w.text).join(' ')
    80→    })),
    81→    bestOccurrence: 0, // First occurrence is usually the "keeper"
    82→    similarity: group.similarity,
    83→    wordCount: opts.phraseSize
    84→  }));
    85→
    86→  // Filter overlapping repetitions
    87→  const filtered = filterOverlappingRepetitions(repetitions);
    88→
    89→  console.log(`[SPLICE Repetition] Found ${filtered.length} repetition group(s)`);
    90→
    91→  return {
    92→    repetitions: filtered,
    93→    metadata: {
    94→      mode: 'basic',
    95→      totalWords: words.length,
    96→      phraseSize: opts.phraseSize,
    97→      tolerance: opts.tolerance,
    98→      groupsFound: filtered.length,
    99→      totalOccurrences: filtered.reduce((sum, r) => sum + r.occurrences.length, 0)
   100→    }
   101→  };
   102→}
   103→
   104→/**
   105→ * Find phrase occurrences using sliding window comparison
   106→ *
   107→ * @param {Array<string>} normalizedWords - Normalized word strings
   108→ * @param {Object} options - Search options
   109→ * @returns {Array} Groups of similar phrases
   110→ */
   111→function findPhraseOccurrences(normalizedWords, options) {
   112→  const { phraseSize, searchRadius, tolerance, minOccurrences } = options;
   113→  const groups = [];
   114→  const usedIndices = new Set();
   115→
   116→  for (let i = 0; i <= normalizedWords.length - phraseSize; i++) {
   117→    if (usedIndices.has(i)) continue;
   118→
   119→    const phrase = normalizedWords.slice(i, i + phraseSize);
   120→    const occurrences = [{
   121→      startIndex: i,
   122→      endIndex: i + phraseSize - 1
   123→    }];
   124→
   125→    // Search ahead for similar phrases
   126→    const searchEnd = Math.min(i + searchRadius, normalizedWords.length - phraseSize);
   127→
   128→    for (let j = i + phraseSize; j <= searchEnd; j++) {
   129→      if (usedIndices.has(j)) continue;
   130→
   131→      const comparePhrase = normalizedWords.slice(j, j + phraseSize);
   132→      const similarity = calculateSimilarity(phrase, comparePhrase);
   133→
   134→      if (similarity >= tolerance) {
   135→        occurrences.push({
   136→          startIndex: j,
   137→          endIndex: j + phraseSize - 1
   138→        });
   139→        // Mark these indices as used
   140→        for (let k = j; k < j + phraseSize; k++) {
   141→          usedIndices.add(k);
   142→        }
   143→      }
   144→    }
   145→
   146→    if (occurrences.length >= minOccurrences) {
   147→      // Mark original phrase indices as used
   148→      for (let k = i; k < i + phraseSize; k++) {
   149→        usedIndices.add(k);
   150→      }
   151→
   152→      groups.push({
   153→        occurrences,
   154→        similarity: 1.0, // First match is reference
   155→        phraseText: phrase.join(' ')
   156→      });
   157→    }
   158→  }
   159→
   160→  return groups;
   161→}
   162→
   163→// =============================================================================
   164→// Core Detection - Advanced Mode (with OpenAI)
   165→// =============================================================================
   166→
   167→/**
   168→ * Detect repetitions with OpenAI boundary refinement
   169→ *
   170→ * @param {Object} transcript - Transcript with words array
   171→ * @param {Object} options - Detection options
   172→ * @returns {Promise<Object>} Detection results with refined boundaries
   173→ */
   174→async function detectRepetitionsAdvanced(transcript, options = {}) {
   175→  const opts = { ...DEFAULT_OPTIONS, ...options, mode: 'advanced' };
   176→
   177→  // First, do basic detection
   178→  const basicResult = detectRepetitionsBasic(transcript, opts);
   179→
   180→  if (basicResult.repetitions.length === 0) {
   181→    return basicResult;
   182→  }
   183→
   184→  // If OpenAI refinement is disabled, return basic results
   185→  if (!opts.useOpenAI) {
   186→    return {
   187→      ...basicResult,
   188→      metadata: { ...basicResult.metadata, mode: 'advanced-local' }
   189→    };
   190→  }
   191→
   192→  // Refine boundaries using OpenAI
   193→  const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
   194→  const refinedRepetitions = [];
   195→
   196→  for (const rep of basicResult.repetitions) {
   197→    try {
   198→      const refined = await refineRepetitionBoundaries(openai, transcript.words, rep);
   199→      refinedRepetitions.push(refined);
   200→    } catch (err) {
   201→      console.warn('[SPLICE Repetition] OpenAI refinement failed, using basic:', err.message);
   202→      refinedRepetitions.push(rep);
   203→    }
   204→  }
   205→
   206→  return {
   207→    repetitions: refinedRepetitions,
   208→    metadata: {
   209→      ...basicResult.metadata,
   210→      mode: 'advanced-openai',
   211→      openaiRefined: true
   212→    }
   213→  };
   214→}
   215→
   216→/**
   217→ * Refine repetition boundaries using OpenAI
   218→ *
   219→ * @param {OpenAI} openai - OpenAI client
   220→ * @param {Array} words - All transcript words
   221→ * @param {Object} repetition - Basic repetition detection
   222→ * @returns {Promise<Object>} Refined repetition with better boundaries
   223→ */
   224→async function refineRepetitionBoundaries(openai, words, repetition) {
   225→  // Get context around the repetition
   226→  const allOccurrences = repetition.occurrences;
   227→  const minStart = Math.max(0, allOccurrences[0].startWord - 5);
   228→  const maxEnd = Math.min(words.length - 1, allOccurrences[allOccurrences.length - 1].endWord + 5);
   229→
   230→  // Build context string with markers
   231→  let contextText = '';
   232→  for (let i = minStart; i <= maxEnd; i++) {
   233→    const word = words[i].word || words[i].text;
   234→    // Mark occurrence boundaries
   235→    const isStart = allOccurrences.some(o => o.startWord === i);
   236→    const isEnd = allOccurrences.some(o => o.endWord === i);
   237→
   238→    if (isStart) contextText += '[';
   239→    contextText += word + ' ';
   240→    if (isEnd) contextText += '] ';
   241→  }
   242→
   243→  const prompt = `Analyze this text for repeated content. The brackets mark detected repetitions:
   244→
   245→"${contextText.trim()}"
   246→
   247→For each bracketed section, determine:
   248→1. Is this truly a repetition/restart/stutter?
   249→2. What are the exact word boundaries that should be removed (keeping only the best take)?
   250→
   251→Respond in JSON format:
   252→{
   253→  "isRepetition": true/false,
   254→  "keepOccurrence": 0-based index of the occurrence to keep (usually the last/most complete),
   255→  "removeRanges": [{"start": wordIndex, "end": wordIndex}]
   256→}`;
   257→
   258→  const response = await openai.chat.completions.create({
   259→    model: 'gpt-4o-mini',
   260→    messages: [
   261→      { role: 'system', content: 'You are an expert video editor identifying repeated content in speech transcripts. Be precise about boundaries.' },
   262→      { role: 'user', content: prompt }
   263→    ],
   264→    temperature: 0,
   265→    max_tokens: 500
   266→  });
   267→
   268→  try {
   269→    const result = JSON.parse(response.choices[0].message.content);
   270→
   271→    if (!result.isRepetition) {
   272→      // False positive - return empty
   273→      return { ...repetition, occurrences: [], metadata: { refined: true, isRepetition: false } };
   274→    }
   275→
   276→    // Update best occurrence
   277→    return {
   278→      ...repetition,
   279→      bestOccurrence: result.keepOccurrence || 0,
   280→      metadata: { refined: true, openaiResult: result }
   281→    };
   282→  } catch (_parseErr) {
   283→    // Return original if parsing fails
   284→    return repetition;
   285→  }
   286→}
   287→
   288→// =============================================================================
   289→// Stutter Detection (Single-Word Repetitions)
   290→// =============================================================================
   291→
   292→/**
   293→ * Detect single-word stutters (e.g., "I I I think")
   294→ *
   295→ * @param {Object} transcript - Transcript with words array
   296→ * @param {Object} options - Detection options
   297→ * @returns {Object} Stutter detection results
   298→ */
   299→function detectStutters(transcript, options = {}) {
   300→  const {
   301→    minRepeats = 2,      // Minimum times word must repeat
   302→    maxGapMs = 500,      // Max gap between words (milliseconds)
   303→    ignoreFillers = true, // Ignore common filler words
   304→    minWordLength = 1    // Minimum word length (1 allows "I", "a", etc.)
   305→  } = options;
   306→
   307→  const words = transcript.words || [];
   308→  const stutters = [];
   309→
   310→  // Common filler words to optionally ignore
   311→  const fillerWords = new Set(['um', 'uh', 'ah', 'er', 'like', 'so', 'well', 'you know']);
   312→
   313→  let i = 0;
   314→  while (i < words.length) {
   315→    const word = words[i];
   316→    const normalized = normalizeWord(word.word || word.text || '');
   317→
   318→    // Skip fillers if configured
   319→    if (ignoreFillers && fillerWords.has(normalized)) {
   320→      i++;
   321→      continue;
   322→    }
   323→
   324→    // Skip very short words (configurable, default allows single chars like "I")
   325→    if (normalized.length < minWordLength) {
   326→      i++;
   327→      continue;
   328→    }
   329→
   330→    // Look for consecutive repeats
   331→    let repeatCount = 1;
   332→    let j = i + 1;
   333→
   334→    while (j < words.length) {
   335→      const nextWord = words[j];
   336→      const nextNormalized = normalizeWord(nextWord.word || nextWord.text || '');
   337→
   338→      // Check if same word
   339→      if (nextNormalized !== normalized) break;
   340→
   341→      // Check gap (convert to ms)
   342→      const gap = (nextWord.start - words[j - 1].end) * 1000;
   343→      if (gap > maxGapMs) break;
   344→
   345→      repeatCount++;
   346→      j++;
   347→    }
   348→
   349→    if (repeatCount >= minRepeats) {
   350→      stutters.push({
   351→        word: normalized,
   352→        occurrences: repeatCount,
   353→        startWord: i,
   354→        endWord: j - 1,
   355→        start: words[i].start,
   356→        end: words[j - 1].end,
   357→        duration: words[j - 1].end - words[i].start,
   358→        // Keep first occurrence, remove rest
   359→        keepStart: words[i].start,
   360→        keepEnd: words[i].end,
   361→        removeStart: words[i + 1].start,
   362→        removeEnd: words[j - 1].end
   363→      });
   364→    }
   365→
   366→    i = j; // Skip past all repeats
   367→  }
   368→
   369→  console.log(`[SPLICE Repetition] Found ${stutters.length} stutter(s)`);
   370→
   371→  return {
   372→    stutters,
   373→    metadata: {
   374→      type: 'stutters',
   375→      totalWords: words.length,
   376→      stutterCount: stutters.length,
   377→      totalRepeatedWords: stutters.reduce((sum, s) => sum + s.occurrences, 0)
   378→    }
   379→  };
   380→}
   381→
   382→// =============================================================================
   383→// Utility Functions
   384→// =============================================================================
   385→
   386→/**
   387→ * Normalize a word for comparison
   388→ */
   389→function normalizeWord(word) {
   390→  return word
   391→    .toLowerCase()
   392→    .replace(/[^\w\s'-]/g, '') // Remove punctuation except apostrophe/hyphen
   393→    .trim();
   394→}
   395→
   396→/**
   397→ * Calculate similarity between two word arrays
   398→ * Uses Jaccard-like similarity with position weighting
   399→ *
   400→ * @param {Array<string>} arr1 - First word array
   401→ * @param {Array<string>} arr2 - Second word array
   402→ * @returns {number} Similarity score (0-1)
   403→ */
   404→function calculateSimilarity(arr1, arr2) {
   405→  if (arr1.length !== arr2.length) return 0;
   406→  if (arr1.length === 0) return 0;
   407→
   408→  let matches = 0;
   409→  for (let i = 0; i < arr1.length; i++) {
   410→    if (arr1[i] === arr2[i]) {
   411→      matches++;
   412→    } else {
   413→      // Partial credit for similar words (edit distance)
   414→      const maxLen = Math.max(arr1[i].length, arr2[i].length);
   415→      if (maxLen > 0) {
   416→        // Early exit: if edit distance is > 30% of max length, similarity < 0.7
   417→        const maxAllowedDist = Math.floor(maxLen * 0.3);
   418→        const editDist = levenshteinDistance(arr1[i], arr2[i], maxAllowedDist);
   419→        if (editDist <= maxAllowedDist) {
   420→          const wordSim = 1 - (editDist / maxLen);
   421→          matches += wordSim;
   422→        }
   423→        // If editDist > maxAllowedDist, wordSim < 0.7, so no credit added
   424→      }
   425→    }
   426→  }
   427→
   428→  return matches / arr1.length;
   429→}
   430→
   431→// Pre-allocated buffers for Levenshtein (avoids allocation per call)
   432→// Max word length we support (longer words use inline allocation)
   433→const LEVENSHTEIN_MAX_LEN = 100;
   434→let _prevRow = new Uint16Array(LEVENSHTEIN_MAX_LEN + 1);
   435→let _currRow = new Uint16Array(LEVENSHTEIN_MAX_LEN + 1);
   436→
   437→/**
   438→ * Levenshtein edit distance between two strings
   439→ * Optimized single-row DP with O(n) space and early exit
   440→ */
   441→function levenshteinDistance(s1, s2, maxThreshold = Infinity) {
   442→  // Fast path: exact match
   443→  if (s1 === s2) return 0;
   444→
   445→  const m = s1.length;
   446→  const n = s2.length;
   447→
   448→  // Fast path: empty string
   449→  if (m === 0) return n;
   450→  if (n === 0) return m;
   451→
   452→  // Early exit if length difference exceeds threshold
   453→  if (Math.abs(m - n) > maxThreshold) return maxThreshold + 1;
   454→
   455→  // Use pre-allocated buffers if possible, otherwise allocate
   456→  let prev, curr;
   457→  if (n < LEVENSHTEIN_MAX_LEN) {
   458→    prev = _prevRow;
   459→    curr = _currRow;
   460→  } else {
   461→    prev = new Uint16Array(n + 1);
   462→    curr = new Uint16Array(n + 1);
   463→  }
   464→
   465→  // Initialize first row
   466→  for (let j = 0; j <= n; j++) prev[j] = j;
   467→
   468→  for (let i = 1; i <= m; i++) {
   469→    curr[0] = i;
   470→    const s1Char = s1.charCodeAt(i - 1);
   471→    let minInRow = i; // Track minimum value in current row for early exit
   472→
   473→    for (let j = 1; j <= n; j++) {
   474→      const cost = s1Char === s2.charCodeAt(j - 1) ? 0 : 1;
   475→
   476→      // curr[j] = min(insertion, deletion, substitution)
   477→      const ins = curr[j - 1] + 1;
   478→      const del = prev[j] + 1;
   479→      const sub = prev[j - 1] + cost;
   480→
   481→      curr[j] = ins < del ? (ins < sub ? ins : sub) : (del < sub ? del : sub);
   482→
   483→      if (curr[j] < minInRow) minInRow = curr[j];
   484→    }
   485→
   486→    // Early exit: if minimum in row exceeds threshold, result will exceed threshold
   487→    if (minInRow > maxThreshold) return maxThreshold + 1;
   488→
   489→    // Swap rows
   490→    const tmp = prev;
   491→    prev = curr;
   492→    curr = tmp;
   493→  }
   494→
   495→  return prev[n];
   496→}
   497→
   498→/**
   499→ * Filter overlapping repetition groups
   500→ */
   501→function filterOverlappingRepetitions(repetitions) {
   502→  if (repetitions.length <= 1) return repetitions;
   503→
   504→  // Sort by first occurrence start
   505→  const sorted = [...repetitions].sort((a, b) =>
   506→    a.occurrences[0].start - b.occurrences[0].start
   507→  );
   508→
   509→  const result = [];
   510→  let lastEnd = -1;
   511→
   512→  for (const rep of sorted) {
   513→    const firstOcc = rep.occurrences[0];
   514→    // Check if overlaps with previous
   515→    if (firstOcc.start >= lastEnd) {
   516→      result.push(rep);
   517→      // Update lastEnd to include all occurrences
   518→      lastEnd = Math.max(...rep.occurrences.map(o => o.end));
   519→    }
   520→  }
   521→
   522→  return result;
   523→}
   524→
   525→/**
   526→ * Convert repetitions to removal segments
   527→ * Keeps the "best" occurrence, returns segments to remove
   528→ *
   529→ * @param {Array} repetitions - Detected repetitions
   530→ * @returns {Array} Segments to remove from timeline
   531→ */
   532→function repetitionsToRemovalSegments(repetitions) {
   533→  const segments = [];
   534→
   535→  for (const rep of repetitions) {
   536→    const keepIndex = rep.bestOccurrence || 0;
   537→
   538→    rep.occurrences.forEach((occ, i) => {
   539→      if (i !== keepIndex) {
   540→        segments.push({
   541→          start: occ.start,
   542→          end: occ.end,
   543→          type: 'repetition',
   544→          text: occ.text,
   545→          reason: `Duplicate of occurrence ${keepIndex + 1}`
   546→        });
   547→      }
   548→    });
   549→  }
   550→
   551→  // Sort by start time and merge adjacent
   552→  return mergeAdjacentSegments(segments);
   553→}
   554→
   555→/**
   556→ * Convert stutters to removal segments
   557→ *
   558→ * @param {Array} stutters - Detected stutters
   559→ * @returns {Array} Segments to remove
   560→ */
   561→function stuttersToRemovalSegments(stutters) {
   562→  return stutters.map(s => ({
   563→    start: s.removeStart,
   564→    end: s.removeEnd,
   565→    type: 'stutter',
   566→    text: `${s.word} (x${s.occurrences})`,
   567→    keepRange: { start: s.keepStart, end: s.keepEnd }
   568→  }));
   569→}
   570→
   571→/**
   572→ * Merge adjacent removal segments
   573→ */
   574→function mergeAdjacentSegments(segments, maxGap = 0.1) {
   575→  if (segments.length <= 1) return segments;
   576→
   577→  const sorted = [...segments].sort((a, b) => a.start - b.start);
   578→  const merged = [];
   579→  let current = { ...sorted[0] };
   580→
   581→  for (let i = 1; i < sorted.length; i++) {
   582→    const next = sorted[i];
   583→
   584→    if (next.start - current.end <= maxGap) {
   585→      // Merge
   586→      current.end = next.end;
   587→      current.text = `${current.text}, ${next.text}`;
   588→    } else {
   589→      merged.push(current);
   590→      current = { ...next };
   591→    }
   592→  }
   593→
   594→  merged.push(current);
   595→  return merged;
   596→}
   597→
   598→// =============================================================================
   599→// Combined Detection
   600→// =============================================================================
   601→
   602→/**
   603→ * Run all repetition detection (phrases + stutters)
   604→ *
   605→ * @param {Object} transcript - Transcript with words array
   606→ * @param {Object} options - Detection options
   607→ * @returns {Promise<Object>} Combined detection results
   608→ */
   609→async function detectAllRepetitions(transcript, options = {}) {
   610→  const opts = { ...DEFAULT_OPTIONS, ...options };
   611→
   612→  // Detect phrase repetitions
   613→  const phraseResult = opts.useOpenAI
   614→    ? await detectRepetitionsAdvanced(transcript, opts)
   615→    : detectRepetitionsBasic(transcript, opts);
   616→
   617→  // Detect stutters
   618→  const stutterResult = detectStutters(transcript, opts);
   619→
   620→  // Combine removal segments
   621→  const phraseSegments = repetitionsToRemovalSegments(phraseResult.repetitions);
   622→  const stutterSegments = stuttersToRemovalSegments(stutterResult.stutters);
   623→  const allSegments = mergeAdjacentSegments([...phraseSegments, ...stutterSegments]);
   624→
   625→  // Calculate total removable time
   626→  const totalRemovableTime = allSegments.reduce((sum, s) => sum + (s.end - s.start), 0);
   627→
   628→  return {
   629→    repetitions: phraseResult.repetitions,
   630→    stutters: stutterResult.stutters,
   631→    removalSegments: allSegments,
   632→    metadata: {
   633→      ...phraseResult.metadata,
   634→      stutterCount: stutterResult.stutters.length,
   635→      totalRemovalSegments: allSegments.length,
   636→      totalRemovableTime: parseFloat(totalRemovableTime.toFixed(3))
   637→    }
   638→  };
   639→}
   640→
   641→// =============================================================================
   642→// Exports
   643→// =============================================================================
   644→
   645→module.exports = {
   646→  // Core detection
   647→  detectRepetitionsBasic,
   648→  detectRepetitionsAdvanced,
   649→  detectStutters,
   650→  detectAllRepetitions,
   651→
   652→  // Conversion utilities
   653→  repetitionsToRemovalSegments,
   654→  stuttersToRemovalSegments,
   655→
   656→  // Helper utilities
   657→  normalizeWord,
   658→  calculateSimilarity,
   659→  levenshteinDistance,
   660→  filterOverlappingRepetitions,
   661→  mergeAdjacentSegments,
   662→
   663→  // Config
   664→  DEFAULT_OPTIONS
   665→};
   666→

