   171→
   172→  let allDBFSValues = [];
   173→  let sampleRateUsed = 16000;
   174→
   175→  // Process each chunk
   176→  for (let i = 0; i < numChunks; i++) {
   177→    const startTime = i * chunkDuration;
   178→    const endTime = Math.min((i + 1) * chunkDuration, duration);
   179→
   180→    console.log(`[SPLICE RMS] Processing chunk ${i + 1}/${numChunks} (${startTime}s - ${endTime}s)`);
   181→
   182→    // Extract chunk audio data
   183→    const chunkData = await extractAudioDataChunk(validatedPath, startTime, endTime - startTime);
   184→    sampleRateUsed = chunkData.sampleRate;
   185→
   186→    // Calculate RMS values for this chunk
   187→    const rmsValues = calculateRMSWindows(chunkData.samples, {
   188→      windowSize: Math.floor(opts.seekStep * chunkData.sampleRate),
   189→      hopSize: Math.floor(opts.seekStep * chunkData.sampleRate / 2)
   190→    });
   191→
   192→    // Convert to dBFS and append efficiently (avoid concat which allocates new array)
   193→    const dBFSValues = rmsValues.map(rms => rmsToDBFS(rms));
   194→    for (let k = 0; k < dBFSValues.length; k++) {
   195→      allDBFSValues.push(dBFSValues[k]);
   196→    }
   197→  }
   198→
   199→  // Determine threshold
   200→  let threshold = opts.threshold;
   201→  let thresholdInfo = { method: 'manual', value: threshold };
   202→
   203→  if (opts.autoThreshold) {
   204→    const autoResult = autoDetectThreshold(allDBFSValues);
   205→    threshold = autoResult.threshold;
   206→    thresholdInfo = autoResult;
   207→    console.log(`[SPLICE RMS] Auto-detected threshold: ${threshold.toFixed(1)}dB`);
   208→  }
   209→
   210→  // Find silence regions
   211→  const rawSilences = findSilenceRegions(allDBFSValues, {
   212→    threshold,
   213→    seekStep: opts.seekStep,
   214→    minSilenceLength: opts.minSilenceLength
   215→  });
   216→
   217→  // Merge adjacent silences
   218→  const mergedSilences = mergeSilences(rawSilences, opts.mergeDistance);
   219→
   220→  // Apply padding
   221→  const paddedSilences = applyPadding(mergedSilences, {
   222→    paddingStart: opts.paddingStart,
   223→    paddingEnd: opts.paddingEnd,
   224→    audioDuration: duration
   225→  });
   226→
   227→  const totalSilenceDuration = paddedSilences.reduce((sum, s) => sum + s.duration, 0);
   228→
   229→  console.log(`[SPLICE RMS] Chunked detection found ${paddedSilences.length} silence(s)`);
   230→
   231→  return {
   232→    silences: paddedSilences,
   233→    metadata: {
   234→      audioDuration: duration,
   235→      sampleRate: sampleRateUsed,
   236→      channels: 1,
   237→      silenceCount: paddedSilences.length,
   238→      totalSilenceDuration,
   239→      speechDuration: duration - totalSilenceDuration,
   240→      silencePercentage: ((totalSilenceDuration / duration) * 100).toFixed(1),
   241→      threshold: thresholdInfo,
   242→      chunked: true,
   243→      chunkCount: numChunks,
   244→      rmsAnalysis: {
   245→        windowCount: allDBFSValues.length,
   246→        ...computeDBFSStats(allDBFSValues)
   247→      }
   248→    }
   249→  };
   250→}
