   165→
   166→// =============================================================================
   167→// Core Detection
   168→// =============================================================================
   169→
   170→/**
   171→ * Detect profanity in transcript words
   172→ *
   173→ * @param {Object} transcript - Transcript with words array
   174→ * @param {Object} options - Detection options
   175→ * @returns {Object} Detection results with marked words and segments
   176→ */
   177→function detectProfanity(transcript, options = {}) {
   178→  const {
   179→    language = 'en',
   180→    customBlocklist = [],
   181→    customAllowlist = [],
   182→    caseSensitive = false,
   183→    includePartialMatches = true,
   184→    frameRate = 30  // For frame alignment
   185→  } = options;
   186→
   187→  // Get base profanity list for language
   188→  const baseProfanity = PROFANITY_LISTS[language] || PROFANITY_LISTS['en'];
   189→
   190→  // Build final profanity set
   191→  const profanitySet = new Set([
   192→    ...baseProfanity,
   193→    ...customBlocklist.map(w => caseSensitive ? w : w.toLowerCase())
   194→  ]);
   195→
   196→  // Remove allowlist items
   197→  const allowlistSet = new Set(customAllowlist.map(w => caseSensitive ? w : w.toLowerCase()));
   198→  for (const allowed of allowlistSet) {
   199→    profanitySet.delete(allowed);
   200→  }
   201→
   202→  // Also add common variants (e.g., "fucking" -> "fuckin'")
   203→  const variants = new Set();
   204→  for (const word of profanitySet) {
   205→    if (word.endsWith('ing')) {
   206→      variants.add(word.slice(0, -1)); // fuckin
   207→      variants.add(word.slice(0, -3) + "in'"); // fuckin'
   208→    }
   209→    if (word.endsWith('ed')) {
   210→      variants.add(word.slice(0, -2) + "'d"); // fuck'd
   211→    }
   212→  }
   213→  for (const v of variants) {
   214→    profanitySet.add(v);
   215→  }
   216→
   217→  // Check each word in transcript
   218→  const words = transcript.words || [];
   219→  const markedWords = [];
   220→
   221→  // Create false positive set once for performance
   222→  const falsePositiveSet = new Set(FALSE_POSITIVE_ALLOWLIST);
   223→
   224→  for (let i = 0; i < words.length; i++) {
   225→    const word = words[i];
   226→    const wordText = word.word || word.text || '';
   227→    const normalizedWord = caseSensitive ? wordText : wordText.toLowerCase();
   228→
   229→    // Remove punctuation for matching
   230→    const cleanWord = normalizedWord.replace(/[^\w\s'-]/g, '').trim();
   231→
   232→    // Check for exact match
   233→    let isProfanity = profanitySet.has(cleanWord);
   234→
   235→    // Check for partial matches (word contains profanity)
   236→    // But skip words that are known false positives
   237→    if (!isProfanity && includePartialMatches) {
   238→      // Skip if word is in the false positive allowlist
   239→      if (!falsePositiveSet.has(cleanWord)) {
   240→        for (const profane of profanitySet) {
   241→          if (cleanWord.includes(profane) && profane.length >= 4) {
   242→            isProfanity = true;
   243→            break;
   244→          }
   245→        }
   246→      }
   247→    }
   248→
   249→    markedWords.push({
   250→      text: wordText,
   251→      start: word.start,
   252→      end: word.end,
   253→      index: i,
   254→      isProfanity,
   255→      // Can be toggled by user in UI
   256→      shouldCensor: isProfanity
   257→    });
   258→  }
   259→
   260→  // Group contiguous profanity into segments
   261→  const segments = groupProfanitySegments(markedWords, { frameRate });
   262→
   263→  // Calculate statistics
   264→  const profanityCount = markedWords.filter(w => w.isProfanity).length;
   265→  const totalDuration = segments.reduce((sum, s) => sum + s.duration, 0);
   266→
   267→  console.log(`[SPLICE Profanity] Detected ${profanityCount} profane word(s) in ${segments.length} segment(s)`);
   268→
   269→  return {
   270→    words: markedWords,
   271→    segments,
   272→    metadata: {
   273→      totalWords: words.length,
   274→      profanityCount,
   275→      segmentCount: segments.length,
   276→      totalCensorDuration: parseFloat(totalDuration.toFixed(3)),
   277→      language,
   278→      customBlocklistCount: customBlocklist.length,
   279→      customAllowlistCount: customAllowlist.length
   280→    }
   281→  };
   282→}
   283→
   284→/**
   285→ * Group contiguous profanity words into segments
   286→ *
   287→ * @param {Array} markedWords - Array of words with isProfanity flag
   288→ * @param {Object} options - Grouping options
   289→ * @returns {Array} Array of profanity segments
   290→ */
   291→function groupProfanitySegments(markedWords, options = {}) {
   292→  const { frameRate = 30, maxGap = 0.3 } = options;
   293→
   294→  const segments = [];
   295→  let currentSegment = null;
   296→
   297→  for (const word of markedWords) {
   298→    if (!word.isProfanity) {
   299→      // End current segment if exists
   300→      if (currentSegment) {
   301→        segments.push(finalizeSeg(currentSegment, frameRate));
   302→        currentSegment = null;
   303→      }
   304→      continue;
   305→    }
   306→
   307→    if (!currentSegment) {
   308→      // Start new segment
   309→      currentSegment = {
   310→        start: word.start,
   311→        end: word.end,
   312→        words: [word.text],
   313→        wordIndices: [word.index]
   314→      };
   315→    } else if (word.start - currentSegment.end <= maxGap) {
   316→      // Extend current segment (words are close together)
   317→      currentSegment.end = word.end;
   318→      currentSegment.words.push(word.text);
   319→      currentSegment.wordIndices.push(word.index);
   320→    } else {
   321→      // Gap too large - finalize current and start new
   322→      segments.push(finalizeSeg(currentSegment, frameRate));
   323→      currentSegment = {
   324→        start: word.start,
   325→        end: word.end,
   326→        words: [word.text],
   327→        wordIndices: [word.index]
   328→      };
   329→    }
   330→  }
   331→
   332→  // Finalize last segment
   333→  if (currentSegment) {
   334→    segments.push(finalizeSeg(currentSegment, frameRate));
   335→  }
   336→
   337→  return segments;
   338→}
   339→
   340→/**
   341→ * Finalize a segment with frame-aligned boundaries
   342→ */
   343→function finalizeSeg(segment, frameRate) {
   344→  // Add small padding around profanity (50ms before, 100ms after)
   345→  const paddedStart = Math.max(0, segment.start - 0.05);
   346→  const paddedEnd = segment.end + 0.1;
   347→
   348→  // Align to frame boundaries
   349→  const alignedStart = alignToFrame(paddedStart, frameRate, 'floor');
   350→  const alignedEnd = alignToFrame(paddedEnd, frameRate, 'ceil');
   351→
   352→  return {
   353→    start: parseFloat(alignedStart.toFixed(4)),
   354→    end: parseFloat(alignedEnd.toFixed(4)),
   355→    duration: parseFloat((alignedEnd - alignedStart).toFixed(4)),
   356→    words: segment.words,
   357→    wordCount: segment.words.length,
   358→    text: segment.words.join(' ')
   359→  };
   360→}
   361→
   362→/**
   363→ * Align time to frame boundary
   364→ */
   365→function alignToFrame(time, frameRate, mode = 'round') {
   366→  const frameDuration = 1 / frameRate;
   367→  switch (mode) {
   368→    case 'floor':
   369→      return Math.floor(time / frameDuration) * frameDuration;
   370→    case 'ceil':
   371→      return Math.ceil(time / frameDuration) * frameDuration;
   372→    default:
   373→      return Math.round(time / frameDuration) * frameDuration;
   374→  }
   375→}
