   422→// =============================================================================
   423→// Bleep Audio Generation
   424→// =============================================================================
   425→
   426→/**
   427→ * Generate a bleep/censor tone audio file
   428→ *
   429→ * @param {Object} options - Generation options
   430→ * @param {number} options.duration - Duration in seconds
   431→ * @param {string} options.bleepType - Type of bleep ('standard', 'tv', 'radio', 'duck', 'mute')
   432→ * @param {number} options.sampleRate - Sample rate (default: 44100)
   433→ * @param {number} options.volume - Volume level 0-1 (default: 0.5)
   434→ * @param {string} options.outputPath - Optional output path (generates temp file if not provided)
   435→ * @returns {Promise<Object>} Result with path to generated file
   436→ */
   437→async function generateBleepAudio(options = {}) {
   438→  const {
   439→    duration = 1.0,
   440→    bleepType = 'standard',
   441→    sampleRate = 44100,
   442→    volume = 0.5,
   443→    outputPath = null
   444→  } = options;
   445→
   446→  const bleepConfig = BLEEP_SOUNDS[bleepType] || BLEEP_SOUNDS.standard;
   447→  const frequency = bleepConfig.frequency;
   448→
   449→  console.log(`[SPLICE Bleep] Generating ${bleepType} bleep: ${duration}s @ ${frequency}Hz`);
   450→
   451→  // Calculate number of samples
   452→  const numSamples = Math.ceil(duration * sampleRate);
   453→  const numChannels = 1; // Mono
   454→  const bitsPerSample = 16;
   455→  const bytesPerSample = bitsPerSample / 8;
   456→
   457→  // Generate audio samples
   458→  const samples = new Int16Array(numSamples);
   459→
   460→  if (frequency > 0) {
   461→    // Generate sine wave at specified frequency
   462→    const angularFrequency = 2 * Math.PI * frequency / sampleRate;
   463→    const amplitude = Math.floor(32767 * volume);
   464→
   465→    // Apply fade in/out to avoid clicks (10ms each)
   466→    const fadeLength = Math.min(Math.floor(0.01 * sampleRate), numSamples / 4);
   467→
   468→    for (let i = 0; i < numSamples; i++) {
   469→      // Base sine wave
   470→      let sample = Math.sin(angularFrequency * i) * amplitude;
   471→
   472→      // Apply fade in
   473→      if (i < fadeLength) {
   474→        sample *= i / fadeLength;
   475→      }
   476→
   477→      // Apply fade out
   478→      if (i > numSamples - fadeLength) {
   479→        sample *= (numSamples - i) / fadeLength;
   480→      }
   481→
   482→      samples[i] = Math.round(sample);
   483→    }
   484→  } else {
   485→    // Mute/duck: generate silence
   486→    samples.fill(0);
   487→  }
   488→
   489→  // Create WAV file
   490→  const dataSize = numSamples * numChannels * bytesPerSample;
   491→  const fileSize = 44 + dataSize; // 44-byte WAV header + data
   492→
   493→  const buffer = Buffer.alloc(fileSize);
   494→
   495→  // WAV header
   496→  buffer.write('RIFF', 0);
   497→  buffer.writeUInt32LE(fileSize - 8, 4);
   498→  buffer.write('WAVE', 8);
   499→  buffer.write('fmt ', 12);
   500→  buffer.writeUInt32LE(16, 16); // Subchunk1Size (16 for PCM)
   501→  buffer.writeUInt16LE(1, 20); // AudioFormat (1 = PCM)
   502→  buffer.writeUInt16LE(numChannels, 22);
   503→  buffer.writeUInt32LE(sampleRate, 24);
   504→  buffer.writeUInt32LE(sampleRate * numChannels * bytesPerSample, 28); // ByteRate
   505→  buffer.writeUInt16LE(numChannels * bytesPerSample, 32); // BlockAlign
   506→  buffer.writeUInt16LE(bitsPerSample, 34);
   507→  buffer.write('data', 36);
   508→  buffer.writeUInt32LE(dataSize, 40);
   509→
   510→  // Write samples
   511→  for (let i = 0; i < numSamples; i++) {
   512→    buffer.writeInt16LE(samples[i], 44 + i * bytesPerSample);
   513→  }
   514→
   515→  // Write to file
   516→  const filePath = outputPath || path.join('/tmp', `splice_bleep_${crypto.randomBytes(4).toString('hex')}.wav`);
   517→
   518→  await fs.promises.writeFile(filePath, buffer);
   519→
   520→  console.log(`[SPLICE Bleep] Generated: ${filePath}`);
   521→
   522→  return {
   523→    success: true,
   524→    path: filePath,
   525→    duration,
   526→    frequency,
   527→    bleepType,
   528→    sampleRate,
   529→    fileSize
   530→  };
   531→}
