   533→/**
   534→ * Generate bleep audio for multiple segments
   535→ *
   536→ * @param {Array} segments - Array of profanity segments with start/end
   537→ * @param {Object} options - Generation options
   538→ * @returns {Promise<Array>} Array of generated bleep info for each segment
   539→ */
   540→async function generateBleepsForSegments(segments, options = {}) {
   541→  const { bleepType = 'standard', volume = 0.5, outputDir = '/tmp' } = options;
   542→
   543→  const results = [];
   544→
   545→  for (let i = 0; i < segments.length; i++) {
   546→    const segment = segments[i];
   547→    const duration = segment.end - segment.start;
   548→
   549→    const outputPath = path.join(outputDir, `splice_bleep_segment_${i}_${crypto.randomBytes(4).toString('hex')}.wav`);
   550→
   551→    try {
   552→      const result = await generateBleepAudio({
   553→        duration,
   554→        bleepType,
   555→        volume,
   556→        outputPath
   557→      });
   558→
   559→      results.push({
   560→        segmentIndex: i,
   561→        segmentStart: segment.start,
   562→        segmentEnd: segment.end,
   563→        ...result
   564→      });
   565→    } catch (err) {
   566→      console.error(`[SPLICE Bleep] Failed to generate bleep for segment ${i}:`, err);
   567→      results.push({
   568→        segmentIndex: i,
   569→        success: false,
   570→        error: err.message
   571→      });
   572→    }
   573→  }
   574→
   575→  return results;
   576→}
