  'crap', 'crappy',
  'hell', 'hellish',
  'piss', 'pissed', 'pissing',

  // Slurs and offensive terms (heavily censored in most media)
  'cunt', 'cunts',
  'cock', 'cocks', 'cocksucker',
  'dick', 'dicks', 'dickhead',
  'pussy', 'pussies',
  'whore', 'whores',
