/**
 * Words that contain profanity substrings but are NOT profane
 * Used to prevent false positives in partial matching
 */
const FALSE_POSITIVE_ALLOWLIST = [
  // Contains "hell"
  'hello', 'shell', 'shellfish', 'shelling', 'michelle', 'mitchell', 'rochelle',
  'seashell', 'eggshell', 'nutshell', 'bombshell', 'dumbbell', 'doorbell',
  'othello', 'cello', 'jello', 'bellow', 'fellow', 'mellow', 'yellow',

  // Contains "ass"
  'class', 'pass', 'mass', 'grass', 'glass', 'brass', 'bass', 'sass',
  'classic', 'passage', 'passenger', 'passive', 'passion', 'passionate',
  'assassin', 'ambassador', 'embassy', 'harass', 'harassment', 'embarrass',
  'compass', 'bypass', 'surpass', 'trespass', 'amass', 'morass',
  'cassette', 'casserole', 'cassandra', 'lasso', 'molasses',
  'massachusetts', 'tallahassee', 'hassle', 'tassel', 'vessel',

  // Contains "damn"
  'amsterdam', 'goddamn', // goddamn is profanity, but amsterdam isn't

  // Contains "crap"
  'scrap', 'scrape', 'scrappy',

  // Contains "cock"
  'peacock', 'hancock', 'cockpit', 'cocktail', 'cockatoo', 'cockatiel',
  'shuttlecock', 'stopcock', 'weathercock', 'woodcock',

  // Contains "dick"
  'dickens', 'benedict', 'addicted', 'predicted', 'verdict',
  'contradiction', 'jurisdiction', 'index',

  // Contains "piss"
  'mississippi',

  // Contains "tit"
  'title', 'titanic', 'constitution', 'institution', 'competition',
  'repetition', 'partition', 'petition', 'nutrition', 'intuition',
  'appetite', 'quantity', 'entity', 'identity', 'activity',

  // Contains "cum"
  'document', 'documentary', 'accumulate', 'circumference', 'cucumber',
  'circumstance', 'incumbent', 'encumber', 'succumb', 'vacuum',

  // Contains "fag"
  'faggot', // this IS profanity, keeping it out

  // Contains "ho"
  'house', 'home', 'honest', 'honor', 'hope', 'hour', 'holiday',
  'whole', 'whose', 'show', 'shoe', 'shop', 'short', 'shot',

  // Contains "nig" - be careful here
  'night', 'knight', 'nightmare', 'nightclub', 'ignite', 'dignity',
  'significant', 'indignant', 'benign', 'malignant',
];

/**
 * Default Spanish profanity list
 */
const DEFAULT_PROFANITY_ES = [
  'mierda', 'puta', 'puto', 'joder', 'coño', 'cojones', 'cabron', 'cabrón',
  'pendejo', 'pendeja', 'chingar', 'chingada', 'verga', 'culo', 'carajo',
  'marica', 'maricón', 'hostia', 'gilipollas', 'capullo', 'hijo de puta'
];

/**
 * Default French profanity list
 */
const DEFAULT_PROFANITY_FR = [
  'merde', 'putain', 'bordel', 'salaud', 'salope', 'connard', 'connasse',
  'enculé', 'nique', 'bite', 'couille', 'foutre', 'baiser', 'chier', 'con'
];

/**
 * Default German profanity list
 */
const DEFAULT_PROFANITY_DE = [
  'scheiße', 'scheisse', 'arschloch', 'hurensohn', 'wichser', 'fotze',
  'fick', 'ficken', 'verdammt', 'schlampe', 'hure', 'mistkerl'
];

// Language mapping
const PROFANITY_LISTS = {
  'en': DEFAULT_PROFANITY_EN,
  'es': DEFAULT_PROFANITY_ES,
  'fr': DEFAULT_PROFANITY_FR,
  'de': DEFAULT_PROFANITY_DE
};

// =============================================================================
// Core Detection
// =============================================================================

/**
 * Detect profanity in transcript words
 *
 * @param {Object} transcript - Transcript with words array
 * @param {Object} options - Detection options
 * @returns {Object} Detection results with marked words and segments
 */
function detectProfanity(transcript, options = {}) {
  const {
    language = 'en',
    customBlocklist = [],
    customAllowlist = [],
    caseSensitive = false,
    includePartialMatches = true,
    frameRate = 30  // For frame alignment
  } = options;

  // Get base profanity list for language
  const baseProfanity = PROFANITY_LISTS[language] || PROFANITY_LISTS['en'];

  // Build final profanity set
  const profanitySet = new Set([
    ...baseProfanity,
    ...customBlocklist.map(w => caseSensitive ? w : w.toLowerCase())
  ]);

  // Remove allowlist items
  const allowlistSet = new Set(customAllowlist.map(w => caseSensitive ? w : w.toLowerCase()));
  for (const allowed of allowlistSet) {
    profanitySet.delete(allowed);
  }

  // Also add common variants (e.g., "fucking" -> "fuckin'")
  const variants = new Set();
  for (const word of profanitySet) {
    if (word.endsWith('ing')) {
      variants.add(word.slice(0, -1)); // fuckin
      variants.add(word.slice(0, -3) + "in'"); // fuckin'
    }
    if (word.endsWith('ed')) {
      variants.add(word.slice(0, -2) + "'d"); // fuck'd
    }
  }
  for (const v of variants) {
    profanitySet.add(v);
  }

  // Check each word in transcript
  const words = transcript.words || [];
  const markedWords = [];

  // Create false positive set once for performance
  const falsePositiveSet = new Set(FALSE_POSITIVE_ALLOWLIST);

  for (let i = 0; i < words.length; i++) {
    const word = words[i];
    const wordText = word.word || word.text || '';
    const normalizedWord = caseSensitive ? wordText : wordText.toLowerCase();

    // Remove punctuation for matching
    const cleanWord = normalizedWord.replace(/[^\w\s'-]/g, '').trim();

    // Check for exact match
    let isProfanity = profanitySet.has(cleanWord);

    // Check for partial matches (word contains profanity)
    // But skip words that are known false positives
    if (!isProfanity && includePartialMatches) {
      // Skip if word is in the false positive allowlist
      if (!falsePositiveSet.has(cleanWord)) {
        for (const profane of profanitySet) {
          if (cleanWord.includes(profane) && profane.length >= 4) {
            isProfanity = true;
            break;
          }
        }
      }
    }

    markedWords.push({
      text: wordText,
      start: word.start,
      end: word.end,
      index: i,
      isProfanity,
      // Can be toggled by user in UI
      shouldCensor: isProfanity
    });
  }

  // Group contiguous profanity into segments
  const segments = groupProfanitySegments(markedWords, { frameRate });

  // Calculate statistics
  const profanityCount = markedWords.filter(w => w.isProfanity).length;
  const totalDuration = segments.reduce((sum, s) => sum + s.duration, 0);

  console.log(`[SPLICE Profanity] Detected ${profanityCount} profane word(s) in ${segments.length} segment(s)`);

  return {
    words: markedWords,
    segments,
    metadata: {
      totalWords: words.length,
      profanityCount,
      segmentCount: segments.length,
      totalCensorDuration: parseFloat(totalDuration.toFixed(3)),
      language,
      customBlocklistCount: customBlocklist.length,
      customAllowlistCount: customAllowlist.length
    }
  };
}

/**
 * Group contiguous profanity words into segments
 *
 * @param {Array} markedWords - Array of words with isProfanity flag
 * @param {Object} options - Grouping options
 * @returns {Array} Array of profanity segments
 */
function groupProfanitySegments(markedWords, options = {}) {
  const { frameRate = 30, maxGap = 0.3 } = options;

  const segments = [];
  let currentSegment = null;

  for (const word of markedWords) {
    if (!word.isProfanity) {
      // End current segment if exists
      if (currentSegment) {
        segments.push(finalizeSeg(currentSegment, frameRate));
        currentSegment = null;
      }
      continue;
    }

    if (!currentSegment) {
      // Start new segment
      currentSegment = {
        start: word.start,
        end: word.end,
        words: [word.text],
        wordIndices: [word.index]
      };
    } else if (word.start - currentSegment.end <= maxGap) {
      // Extend current segment (words are close together)
      currentSegment.end = word.end;
      currentSegment.words.push(word.text);
      currentSegment.wordIndices.push(word.index);
    } else {
      // Gap too large - finalize current and start new
      segments.push(finalizeSeg(currentSegment, frameRate));
      currentSegment = {
        start: word.start,
        end: word.end,
        words: [word.text],
        wordIndices: [word.index]
      };
    }
  }

  // Finalize last segment
  if (currentSegment) {
    segments.push(finalizeSeg(currentSegment, frameRate));
  }

  return segments;
}

/**
 * Finalize a segment with frame-aligned boundaries
 */
function finalizeSeg(segment, frameRate) {
  // Add small padding around profanity (50ms before, 100ms after)
  const paddedStart = Math.max(0, segment.start - 0.05);
  const paddedEnd = segment.end + 0.1;

  // Align to frame boundaries
  const alignedStart = alignToFrame(paddedStart, frameRate, 'floor');
  const alignedEnd = alignToFrame(paddedEnd, frameRate, 'ceil');

  return {
    start: parseFloat(alignedStart.toFixed(4)),
    end: parseFloat(alignedEnd.toFixed(4)),
    duration: parseFloat((alignedEnd - alignedStart).toFixed(4)),
    words: segment.words,
    wordCount: segment.words.length,
    text: segment.words.join(' ')
  };
}

/**
 * Align time to frame boundary
 */
function alignToFrame(time, frameRate, mode = 'round') {
  const frameDuration = 1 / frameRate;
  switch (mode) {
    case 'floor':
      return Math.floor(time / frameDuration) * frameDuration;
    case 'ceil':
      return Math.ceil(time / frameDuration) * frameDuration;
    default:
      return Math.round(time / frameDuration) * frameDuration;
  }
}

// =============================================================================
// Bleep Sound Management
// =============================================================================

/**
 * Available bleep sounds
 */
const BLEEP_SOUNDS = {
  'standard': {
    name: 'Standard Censor Beep',
    frequency: 1000,
    description: 'Classic 1kHz censor beep'
  },
  'tv': {
    name: 'TV Bleep',
    frequency: 1000,
    description: 'Television-style censor beep'
  },
  'radio': {
    name: 'Radio Bleep',
    frequency: 880,
    description: 'Radio broadcast censor tone'
  },
  'duck': {
    name: 'Audio Duck',
    frequency: 0,
    description: 'Volume reduction (no beep)'
  },
  'mute': {
    name: 'Complete Mute',
    frequency: 0,
    description: 'Complete silence'
  }
};

/**
 * Get list of available bleep sounds
 */
function getAvailableBleepSounds() {
  return Object.entries(BLEEP_SOUNDS).map(([id, info]) => ({
    id,
    ...info
  }));
}

// =============================================================================
// Bleep Audio Generation
// =============================================================================

/**
 * Generate a bleep/censor tone audio file
 *
 * @param {Object} options - Generation options
 * @param {number} options.duration - Duration in seconds
 * @param {string} options.bleepType - Type of bleep ('standard', 'tv', 'radio', 'duck', 'mute')
 * @param {number} options.sampleRate - Sample rate (default: 44100)
 * @param {number} options.volume - Volume level 0-1 (default: 0.5)
 * @param {string} options.outputPath - Optional output path (generates temp file if not provided)
 * @returns {Promise<Object>} Result with path to generated file
 */
async function generateBleepAudio(options = {}) {
  const {
    duration = 1.0,
    bleepType = 'standard',
    sampleRate = 44100,
    volume = 0.5,
    outputPath = null
  } = options;

  const bleepConfig = BLEEP_SOUNDS[bleepType] || BLEEP_SOUNDS.standard;
  const frequency = bleepConfig.frequency;

  console.log(`[SPLICE Bleep] Generating ${bleepType} bleep: ${duration}s @ ${frequency}Hz`);

  // Calculate number of samples
  const numSamples = Math.ceil(duration * sampleRate);
  const numChannels = 1; // Mono
  const bitsPerSample = 16;
  const bytesPerSample = bitsPerSample / 8;

  // Generate audio samples
  const samples = new Int16Array(numSamples);

  if (frequency > 0) {
    // Generate sine wave at specified frequency
    const angularFrequency = 2 * Math.PI * frequency / sampleRate;
    const amplitude = Math.floor(32767 * volume);

    // Apply fade in/out to avoid clicks (10ms each)
    const fadeLength = Math.min(Math.floor(0.01 * sampleRate), numSamples / 4);

    for (let i = 0; i < numSamples; i++) {
      // Base sine wave
      let sample = Math.sin(angularFrequency * i) * amplitude;

      // Apply fade in
      if (i < fadeLength) {
        sample *= i / fadeLength;
      }

      // Apply fade out
      if (i > numSamples - fadeLength) {
        sample *= (numSamples - i) / fadeLength;
      }

      samples[i] = Math.round(sample);
    }
  } else {
    // Mute/duck: generate silence
    samples.fill(0);
  }

  // Create WAV file
  const dataSize = numSamples * numChannels * bytesPerSample;
  const fileSize = 44 + dataSize; // 44-byte WAV header + data

  const buffer = Buffer.alloc(fileSize);

  // WAV header
  buffer.write('RIFF', 0);
  buffer.writeUInt32LE(fileSize - 8, 4);
  buffer.write('WAVE', 8);
  buffer.write('fmt ', 12);
  buffer.writeUInt32LE(16, 16); // Subchunk1Size (16 for PCM)
  buffer.writeUInt16LE(1, 20); // AudioFormat (1 = PCM)
  buffer.writeUInt16LE(numChannels, 22);
  buffer.writeUInt32LE(sampleRate, 24);
  buffer.writeUInt32LE(sampleRate * numChannels * bytesPerSample, 28); // ByteRate
  buffer.writeUInt16LE(numChannels * bytesPerSample, 32); // BlockAlign
  buffer.writeUInt16LE(bitsPerSample, 34);
  buffer.write('data', 36);
  buffer.writeUInt32LE(dataSize, 40);

  // Write samples
  for (let i = 0; i < numSamples; i++) {
    buffer.writeInt16LE(samples[i], 44 + i * bytesPerSample);
  }

  // Write to file
  const filePath = outputPath || path.join('/tmp', `splice_bleep_${crypto.randomBytes(4).toString('hex')}.wav`);

  await fs.promises.writeFile(filePath, buffer);

  console.log(`[SPLICE Bleep] Generated: ${filePath}`);

  return {
    success: true,
    path: filePath,
    duration,
    frequency,
    bleepType,
    sampleRate,
    fileSize
  };
}

/**
 * Generate bleep audio for multiple segments
 *
 * @param {Array} segments - Array of profanity segments with start/end
 * @param {Object} options - Generation options
 * @returns {Promise<Array>} Array of generated bleep info for each segment
 */
async function generateBleepsForSegments(segments, options = {}) {
  const { bleepType = 'standard', volume = 0.5, outputDir = '/tmp' } = options;

  const results = [];

  for (let i = 0; i < segments.length; i++) {
    const segment = segments[i];
    const duration = segment.end - segment.start;

    const outputPath = path.join(outputDir, `splice_bleep_segment_${i}_${crypto.randomBytes(4).toString('hex')}.wav`);

    try {
      const result = await generateBleepAudio({
        duration,
        bleepType,
        volume,
        outputPath
      });

      results.push({
        segmentIndex: i,
        segmentStart: segment.start,
        segmentEnd: segment.end,
        ...result
      });
    } catch (err) {
      console.error(`[SPLICE Bleep] Failed to generate bleep for segment ${i}:`, err);
      results.push({
        segmentIndex: i,
        success: false,
        error: err.message
      });
    }
  }

  return results;
}

/**
 * Clean up generated bleep files
 *
 * @param {Array} bleepPaths - Array of bleep file paths to delete
 */
async function cleanupBleepFiles(bleepPaths) {
  for (const filePath of bleepPaths) {
    try {
      await fs.promises.unlink(filePath);
    } catch (_err) {
      // Ignore errors (file may already be deleted)
    }
  }
}

// =============================================================================
// Profanity List Management
// =============================================================================

/**
 * Get the default profanity list for a language
 *
 * @param {string} language - Language code (en, es, fr, de)
 * @returns {Array<string>} Profanity word list
 */
function getProfanityList(language = 'en') {
  return PROFANITY_LISTS[language] || PROFANITY_LISTS['en'];
}

/**
 * Get all supported languages
 */
function getSupportedLanguages() {
  return [
    { code: 'en', name: 'English' },
    { code: 'es', name: 'Spanish' },
    { code: 'fr', name: 'French' },
    { code: 'de', name: 'German' }
  ];
}

/**
 * Validate and clean a custom word list
 *
 * @param {string|Array} input - Comma-separated string or array
 * @returns {Array<string>} Cleaned word list
 */
function parseWordList(input) {
  if (!input) return [];

  if (Array.isArray(input)) {
    return input.map(w => w.trim().toLowerCase()).filter(w => w.length > 0);
  }

  if (typeof input === 'string') {
    return input
      .split(',')
      .map(w => w.trim().toLowerCase())
      .filter(w => w.length > 0);
  }

  return [];
}

// =============================================================================
// Segment Operations
// =============================================================================

/**
 * Merge profanity segments that are close together
 *
 * @param {Array} segments - Array of profanity segments
 * @param {number} maxGap - Maximum gap to merge (seconds)
 * @returns {Array} Merged segments
 */
function mergeAdjacentSegments(segments, maxGap = 0.5) {
  if (segments.length <= 1) return segments;

  const sorted = [...segments].sort((a, b) => a.start - b.start);
  const merged = [];
  let current = { ...sorted[0] };

  for (let i = 1; i < sorted.length; i++) {
    const next = sorted[i];

    if (next.start - current.end <= maxGap) {
      // Merge
      current.end = next.end;
      current.duration = current.end - current.start;
      current.words = [...current.words, ...next.words];
      current.wordCount = current.words.length;
      current.text = current.words.join(' ');
    } else {
      merged.push(current);
      current = { ...next };
    }
  }

  merged.push(current);
  return merged;
}

/**
 * Filter segments by user selection (for UI toggle)
 *
 * @param {Array} segments - All profanity segments
 * @param {Array<number>} selectedIndices - Indices of segments to keep
 * @returns {Array} Filtered segments
 */
function filterSelectedSegments(segments, selectedIndices) {
  const indexSet = new Set(selectedIndices);
  return segments.filter((_, i) => indexSet.has(i));
}

/**
 * Convert segments to timeline format for cut list generator
 *
 * @param {Array} segments - Profanity segments
 * @returns {Array<{start: number, end: number, type: string}>} Timeline-ready segments
 */
function segmentsToTimeline(segments) {
  return segments.map(seg => ({
    start: seg.start,
    end: seg.end,
    duration: seg.duration,
    type: 'profanity',
    label: `Profanity: ${seg.text.substring(0, 30)}${seg.text.length > 30 ? '...' : ''}`
  }));
}

// =============================================================================
// Exports
// =============================================================================

module.exports = {
  // Core detection
  detectProfanity,
  groupProfanitySegments,

  // Profanity lists
  getProfanityList,
  getSupportedLanguages,
  parseWordList,
  PROFANITY_LISTS,

  // Bleep sounds
  getAvailableBleepSounds,
  BLEEP_SOUNDS,

  // Bleep audio generation
  generateBleepAudio,
  generateBleepsForSegments,
  cleanupBleepFiles,

  // Segment operations
  mergeAdjacentSegments,
  filterSelectedSegments,
  segmentsToTimeline,

  // Utilities
  alignToFrame
};

