async function generateBleepsForSegments(segments, options = {}) {
  const { bleepType = 'standard', volume = 0.5, outputDir = '/tmp' } = options;

  // Generate all bleeps in parallel (I/O bound, safe to parallelize)
  const results = await Promise.all(
    segments.map(async (segment, i) => {
      const duration = segment.end - segment.start;
      const outputPath = path.join(outputDir, `splice_bleep_segment_${i}_${crypto.randomBytes(4).toString('hex')}.wav`);

      try {
        const result = await generateBleepAudio({ duration, bleepType, volume, outputPath });
