   626→      jCutLeadOut: 0.2           // Audio extends 0.2s after video
   627→    }
   628→  },
   629→
   630→  // Interview - balanced, respects speaker pauses
   631→  // J-Cut enabled for smooth speaker transitions
   632→  interview: {
