  1268→/**
  1269→ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
  1270→ * Character set excludes confusing chars: 0/O, 1/I/L
  1271→ * @param {string} key - License key to validate
  1272→ * @returns {boolean} True if format is valid
  1273→ */
  1274→function isValidLicenseKeyFormat(key) {
  1275→  if (!key || typeof key !== 'string') return false;
  1276→  const normalized = key.toUpperCase().trim();
  1277→  const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
  1278→  return pattern.test(normalized);
  1279→}
