  1290→  const creditBadge = document.getElementById('creditBadge');
  1291→
  1292→  // Handle credit badge click - retry on error, or show login modal
  1293→  if (creditBadge) {
  1294→    creditBadge.addEventListener('click', async () => {
  1295→      // If in error state, retry fetching credits
  1296→      if (creditBadge.classList.contains('error')) {
