  1303→      // Otherwise show login modal
  1304→      showLoginModal();
  1305→    });
  1306→  }
  1307→
  1308→  // Close login modal
  1309→  if (closeLoginBtn && loginModal) {
  1310→    closeLoginBtn.addEventListener('click', () => {
  1311→      loginModal.classList.add('hidden');
