  1322→  }
  1323→
  1324→  // Activate license key
  1325→  if (saveLoginBtn) {
  1326→    saveLoginBtn.addEventListener('click', async () => {
  1327→      const licenseKey = licenseKeyInput?.value?.trim()?.toUpperCase();
  1328→
  1329→      if (!licenseKey) {
  1330→        showLoginError('Please enter your license key');
