  1545→
  1546→const PROFANITY_SETTINGS_KEY = 'spliceProfanitySettings';
  1547→
  1548→/**
  1549→ * Default profanity settings
  1550→ */
  1551→const DEFAULT_PROFANITY_SETTINGS = {
  1552→  enabled: false,
  1553→  language: 'en',
  1554→  customBlocklist: [],
  1555→  customAllowlist: [],
  1556→  bleepType: 'standard'
  1557→};
  1558→
  1559→/**
  1560→ * Get profanity settings from localStorage
